INSERT INTO `smart_article_article` VALUES (23, 42, 4, 1, 'en', '2005-09-02 13:20:00', '2005-09-02 13:20:39', '2006-01-26 10:41:01', 0, 0, 'Actions', '', '', '', '', '<p>Dieses Modul stallt eine Reihe von Action-Klassen zur Verfügung die in Projekt-Views verwendet werden können.<br />      </p>            <h5>getArticle</h5>          <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten eines Artikels<br />      </p>               <p><strong>Syntax:</strong></p>               <pre>$this->model->action(''article'', ''getArticle'',<br />                      array(''id_article'' => integer,<br />                            ''result''     => & array(),<br />                            ''pubdate''    => array(''>|<|=|>=|<=|!='', <br />                                                  ''CURRENT_TIMESTAMP''),//optional<br />                            ''fields''     => array(''title'',''overtitle'',<br />                                                  ''subtitle'',''header'',<br />                                                  ''description'',''body'',<br />                                                  ''ps'',''pubdate'',<br />                                                  ''articledate'',''modifydate'',<br />                                                  ''logo'',''media_folder'') ));</pre>   <h5> </h5> <h5> </h5> <h5>getNodeArticles</h5>                 <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten von Artikeln die sich unterhalb eines Navigationsknoten befinden<br />      </p>         <p><strong>Syntax:</strong></p>          <pre>$this->model->action(''article'', ''getNodeArticles'',<br />                     array(''id_node'' => integer,<br />                           ''result''  => & array,<br />                           ''status''  => array(''>|<|=|>=|<=|!='', <br />                                              0|1|2|3|4),// optional<br />                           ''pubdate'' => array(''>|<|=|>=|<=|!='', <br />                                              ''CURRENT_TIMESTAMP''),//optional<br />                           ''order''   => array(''rank|title|<br />                                              articledate|pubdate|modifydate<br />                                              overtitle|subtitle'', <br />                                              ''asc|desc''),  // optional<br />                           ''fields''  => array(''title'',''overtitle'',<br />                                               ''subtitle'',''header'',<br />                                               ''description'',''body'',<br />                                               ''ps'',''pubdate'',<br />                                               ''articledate'',''modifydate'',<br />                                               ''logo'',''media_folder'') ));</pre>     <p> </p>     <h5><strong>search</strong></h5>    <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten von Artikeln die einem Suchkriterium entsprechen<br />      </p>        <p><strong>Syntax:</strong></p>        <pre>$this->model->action(''article'', ''search'',<br />                     array(''result''  => & array,<br />                           ''search''  => string,   // search string<br />                           ''status''  => array( ''>|<|=|>=|<=|!='',<br />                                                0|1|2),// optional<br />                           ''nodeStatus'' => array( ''>|<|=|>=|<=|!='',<br />                                                0|1|2|3|4 ),// optional<br />                           ''pubdate'' => array(''>|<|=|>=|<=|!='', <br />                                              ''CURRENT_TIMESTAMP''),//optional<br />                           ''order''   => array(''rank|title|<br />                                              articledate|pubdate|modifydate<br />                                              overtitle|subtitle'', <br />                                              ''asc|desc''),  // optional<br />                           ''fields''  => array(''title'',''overtitle'',''id_node'',<br />                                              ''subtitle'',''header'',<br />                                              ''description'',''body'',<br />                                              ''ps'',''pubdate'',<br />                                              ''articledate'',''modifydate'',<br />                                              ''logo'',''media_folder'') ));</pre> <h5>pager</h5>          <p><strong>Beschreibung:</strong> Erstellt eine Zeichenkette die Paginationlinks enthält<br />      </p>               <p><strong>Syntax:</strong></p> <pre>$this->model->action(''article'',''pager'', <br />                      array(''result''     => & array,<br />                            ''id_node''    => integer,<br />                            ''search''     => string, // search string<br />                            ''status''     => array(''>|<|=|>=|<=|!='',<br />                                                  0|1|2|3|4 ),// optional   <br />                            ''nodeStatus'' => array(''>|<|=|>=|<=|!='',<br />                                                  0|1|2),   // optional                    <br />                            ''pubdate''    => array(''>|<|=|>=|<=|!='', <br />                                                  ''CURRENT_TIMESTAMP''),//optional<br />                            ''perPage''    => integer,<br />                            ''numPage''    => integer,<br />                            ''delta''      => integer,<br />                            ''url''        => string,<br />                            ''var_prefix'' => string,<br />                            ''css_class''  => string));  </pre> <p> </p> <h5>getStatus</h5>            <p><strong>Beschreibung:</strong> Gibt den Status eines Artikels und den Status des Artikel zugeörigen Knotens.<br />  $result[''nodeStatus'']<br />  $result[''articleStatus'']<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre>$model->action(''article'',''getStatus'', <br />                array(''id_article'' => int,<br />                      ''result''     => & array));    </pre><h5>getKeywordIds</h5>            <p><strong>Beschreibung:</strong> Gibt alle SchlüsselwortID''s zurück die mit einem ArticleID verknüpft sind<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''article'', ''getKeywordIds'',<br />                 array(''result''     => &$array(),<br />                       ''id_article'' => int,<br />                       ''key_status'' => array(''>|<|=|>=|<=|!='',<br />                                             1|2) // keyword status - optional<br />                       ) );<br /></pre><p> </p><h5>fromKeyword</h5>            <p><strong>Beschreibung:</strong> Gibt alle Artikel, die mit den SchlüsselwortId''s verknüpft sind die in einem Array definiert sind.<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''article'',''fromKeyword'',<br />                 array(''id_key_list'' => array( int, int,..,..,..),<br />                       ''result''      => & array,<br />                       ''status''      => array(''>|<|=|>=|<=|!='',<br />                                              1|2|3|4|5), // article status - optional<br />                       ''key_status''  => array(''>|<|=|>=|<=|!='',<br />                                              1|2), // keyword status - optional<br />                       ''node_status'' => array(''>|<|=|>=|<=|!='',<br />                                              1|2), // navigation node status -  optional<br />                       ''exclude''     => array( integers ), // exclude id_articles''s optional<br />                       ''order''   => array(''rank|title|<br />                                           articledate|pubdate|<br />                                           overtitle|subtitle'', ''asc|desc''), // optional<br />                       ''disable_sql_cache'' => TRUE,  // optional <br />                       ''fields   => array(''id_node'',''id_article'',''status'',''rank'',<br />                                          ''activedate'',''inactivedate'',''pubdate'',<br />                                          ''lang'',''title'',''overtitle'',<br />                                          ''subtitle'',''header'',''description'',<br />                                          ''body'',''ps'',''fulltextbody'',<br />                                          ''format'',''media_folder'') ));</pre>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (2, 20, 4, 1, 'en', '2005-09-01 16:24:00', '2005-09-01 16:24:56', '2006-02-19 18:18:26', 0, 0, 'Installation', '', '', '', '', '<div class="text"><p>The installation of Smart3 is simple. Upload the extracted files to a folder somewere under the document root of the webserver. Create a MySql 4.1 DataBase. Point your browser to the admin.php file (Admin controller). You should see a setup formular. Follow the instructions.</p> <p>The following folders must be writeable by php scripts:</p>   <ul><li>/cache</li><li>/cache/smartyCompiled</li> <li>/config</li><li>/data</li><li>/data/article</li><li>/data/article/rss<br /></li> <li>/data/common</li><li>/data/common/captcha</li><li>/data/common/rss_cache</li><li>/data/common/tinymce_cache</li><li>/data/misc</li><li>/data/navigation</li><li>/data/user<br />   </li><li>/logs</li></ul> <p>&nbsp;</p><p>If it is the first time you use Smart3 and you want to test it you should select &quot;insert sample content&quot; at the setup screen. After successfull submiting the form you are forwarded to the login of Smart3. </p><p>You can find the public views and templates in:</p><p><strong>/views_smart</strong></p><p><strong>/templates_smart </strong></p><p>&nbsp;</p><p>Enjoy! </p></div>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (3, 20, 4, 2, 'en', '2005-09-01 16:27:00', '2005-09-01 16:27:19', '2006-01-24 22:03:21', 0, 0, 'The Action Class', '', '', '', '', '<p>An action class represents the model of a modul. Every model related operations are executed by actions. An action class is indirectly executed by the methode action() of the model class:</p>   <pre><strong>$model->action</strong>(''modulename'',''actionname'', & $data_array ); </pre>   <p class="undefined">The method of the model class include the target action class file, makes an instance of this class, execute its validate() methode and if successfull its perform() method.</p>   <h4>Data validation </h4>   <p>An action class has a defensive character. This means, that before the perform() methode works on and with the data passed as an array, the vallidate() methode checks if the content of this data array is valide.</p>   <pre>public function validate( $data = FALSE ){}; </pre>   <p>The validate() methode must return bool TRUE so that the perform() methode is executed by the model action methode.</p>   <h4>Design of an Action Class<br /> </h4>   <p>An action class always depends (extends) on a parent class "SmartAction". The class name must follow the convention:</p>   <p>class Action[Modulname][Actionname] extends SmartAction {}</p>   <p>The file name and folder of such a class must follow the convention:</p>   <p>/modules/xxx/action/Action[Modulename][Actionname].php</p>   <p>Once a file with the related action class code is in the right module <strong>/action</strong> folder, you can use such an action from within any part of the system.</p> <p>Here a minimalistic action class: </p>   <pre>class ActionCommonTest extends SmartAction<br />{<br />    /**<br />     * perform<br />     *<br />     * @param mixed $data<br />     */<br />    public function perform( $data = FALSE )<br />    {<br />    }<br />    <br />    /**<br />     * Validate data passed to this action<br />     * @param mixed $data<br />     * @return bool<br />     */<br />    public function validate( $data = FALSE )<br />    {<br />        return TRUE;<br />    }    <br />} </pre>   <p>When saving this class as /modules/common/action/ActionCommonTest.php and you try to use it, it does nothing.</p>   <pre>$this->model->action( ''common'', ''test'', array() );</pre>   <p> Therefore her an action class which trims an given string:<br /> </p>   <pre> class ActionCommonTrim extends SmartAction<br /> {<br />    /**<br />     * Trim a string<br />     *<br />     * @param mixed $data<br />     */<br />    public function perform( $data = FALSE )<br />    {<br />        $data[''result''] = trim( $data[''str''] ); <br />    }<br />    <br />    /**<br />     * Validate data passed to this action<br />     */<br />    public function validate( $data = FALSE )<br />    {<br />        if(!isset($data[''error'']))<br />        {<br />            throw new SmartModelException(''Missing "error" var''); <br />        }<br /><br />        if(!isset($data[''result'']))<br />        {<br />            $data[''error''] = "array ''result'' key is not defined!";<br />            return FALSE;<br />        }<br /><br />        if(!isset($data[''str'']))<br />        {<br />            $data[''error''] = "array ''str'' key is not defined!";<br />            return FALSE;<br />        }<br /><br />        if(!is_string($data[''str'']))<br />        {<br />            $data[''error''] = "array ''str'' value is not from type string!";<br />            return FALSE;<br />        }<br />        return TRUE;<br />    }    <br />}</pre>   <p>Save this class as /modules/common/action/ActionCommonTrim.php and use it as follows from within a public view class:</p>     <pre>// init variables<br />$trimmed_string = '''';<br />$error = '''';<br /><br />$this->model->action( ''common'', ''trim'',<br />                      array(''result'' => & $trimmed_string,<br />                            ''str''    => ''  This is a test    '',<br />                            ''error''  => & $error)); <br /><br />if(!empty($error))<br />{<br />    echo $error;<br />} <br />else<br />{<br />    echo $trimmed_string;<br />} </pre>   <p>The biggest part is the code of the validate() methode. There are more possible error handling ways. </p>   <ul> <li>An action class couldnt be executed at all without disturbing the whole system. In this case you have to throw an exception. This happens if a view programmer does an action call in a wrong manner (wrong parameter type). It''s smilar to an php fatal error.</li><li>The action caller handle the error it self. Here the validate methode assign an given error array with the error messages. The validate methode must return bool FALSE.</li> </ul>   <p>It is important to know that if an action should return some data you have to pass the result array as a reference:</p>   <pre>$model->action( ''xxx'', ''yyy'', array( ''result'' => <strong>& $result_array</strong>, ''error'' => <strong>& $error_array</strong> ) ); </pre>   <h4>Class variables </h4>   <p>The model instance assign the following variables of an action class:</p>   <ul> <li>$this->model - Instance of the model class. You need this to execute other action classes from within the current action class.<br /> </li><li>$this->config - Global configuration array.</li><li>$this->constructorData - Data that is passed to the action class constructor.</li> </ul>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (4, 20, 4, 3, 'en', '2005-09-01 16:28:00', '2005-09-01 16:28:41', '2006-01-24 22:03:20', 0, 0, 'The View Class', '', '', '', '', '<p>It is the charge of the controller to include this class, to make an instance and to execute, in a well defined order, some methods of a view class. Your charge (the view programmer) is to fill the view methods with code that reacts on a web request. In other words; your make such a view class alive. </p>   <p>The order in which the controller execute the view methods:</p>     <ul> <li><strong> auth()</strong> - if needed you can include here some authentication stuff. (optional)<br /> </li><li><strong>prependFilterChain()</strong> - ... example: spam boot detection, logging, filtering the superglobal request variables. (optional)<br /> </li><li><strong>perform() </strong>- Here the real stuff happens. (optional)<br /> </li><li><strong>appendFilterChain( & $tplBufferContent )</strong> - This methode is executed after a template was rendered. So you can do some work on the template output. (optional)<br /> </li> </ul>   <p>Furthermore there are a couple of class variables which you should keep in mind:</p>     <ul> <li><strong> $tplVar</strong> - In this array you have to assign all variables that are used in a template. Ex.: $this->tplVar[''title''] = ''Hello World''; You can use this variable in a template as $tpl[''title''].</li><li><strong>$viewVar</strong> - In this array you have to assign all variables that may be used in nested views. <br /> </li><li><strong>$model</strong> - The instance of the model class. Needed to execute actions. Ex.: $this->model->action(''common'',''test'');</li><li><strong>$session</strong> - The instance of the session class<br /> </li><li><strong>$config</strong> - Array that contains all global config variables.</li><li><strong>$template</strong> - A string variable in which you can define the name of the template if it is else than the default name.</li><li><strong>$renderTemplate</strong> - This var is from type BOOL. If it is FALSE then no template is going to render.</li><li><strong>$templateFolder</strong> - If the template folder is an other one than the default you have to define the absolute path here.</li><li><strong>$templateEngine</strong> - Here you can define the template engine type if you want to use an other than defined in the configuration array. Currently there are 2 types:  ''SmartTplContainerPhp'' (default) and ''SmartTplContainerSmarty''</li> <li><strong>$viewData</strong> - It is possible that the controller pass some data to the view. You will find this data here.</li><li><strong>$cacheExpire</strong> - You can define the time in seconds the template cache expires. 0 means cache disabled.</li><li><strong>$</strong><strong>cacheId</strong> - Usually the cache ID is generated by the system. But here you can define your own cache ID.<br />   </li>  </ul>   <p>You can find the public view classes in the root folder <strong>/views_xxx</strong>. Where xxx is a name of your choice. It is possible to create more public view folders. The global config array contains a variable with the current active view folder.</p>     <h5>Example of a view class.</h5>     <p>This example show you a part of the view class in ViewNode.php file. The job of this view is to retrive data of a requested navigation node, its child nodes and the branch of this node. The methode "prependFilterChain()" checks if the superglobal var "id_node" contains only numbers. If not an error template is rendered. This methode furthermore sends a http header to disable browser caching.<br /> </p>     <pre>class ViewNode extends SmartView<br />{<br />    /**<br />     * Cache expire time in seconds for this view<br />     * 0 = cache disabled<br />     */<br />    public $cacheExpire = 3600;<br />    <br />    /**<br />     * Execute the view of the "node" template<br />     */<br />    function perform()<br />    { <br />        // init variables (see private function below)<br />        $this->initVars();<br />          <br />        // get requested node content<br />        $this->model->action(''navigation'',''getNode'', <br />                             array(''result''  => & $this->tplVar[''node''],<br />                                   ''id_node'' => (int)$this->current_id_node,<br />                                   ''fields''  => array(''title'',''body'',<br />                                                      ''id_node'',''media_folder'')));<br /><br />        // get child nodes content of the requested node<br />        // only with status=2, means active      <br />        $this->model->action(''navigation'',''getChilds'', <br />                             array(''result''  => & $this->tplVar[''childNodes''],<br />                                   ''id_node'' => (int)$this->current_id_node,<br />                                   ''status''  => array(''='',2),<br />                                   ''fields''  => array(''title'',''short_text'',''id_node'')));<br /> <br />        // get navigation node branch content of the requested node<br />        $this->model->action(''navigation'',''getBranch'', <br />                             array(''result''  => & $this->tplVar[''nodeBranch''],<br />                                   ''id_node'' => (int)$this->current_id_node,<br />                                   ''fields''  => array(''title'',''id_node'')));                   <br />    }<br /><br />    /**<br />     * prepend filter chain<br />     *<br />     */<br />    public function prependFilterChain()<br />    {<br />        // fetch the current id_node. <br />        // If no id_node defined, not a string or not number chars<br />        // this view class loads the error template<br />        //<br />        if( !isset($_REQUEST[''id_node''])     || <br />            !is_string($_REQUEST[''id_node'']) || <br />            preg_match("/[^0-9]+/",$_REQUEST[''id_node'']) ) <br />        {<br />            $this->template  = ''error'';     <br />        }<br />        else<br />        {<br />            $this->current_id_node = (int)$_REQUEST[''id_node''];          <br />        }<br />        <br />        // filter action of the common module to prevent browser caching<br />        $this->model->action( ''common'', ''filterDisableBrowserCache'');    <br />    }<br /><br />    /**<br />     * append filter chain<br />     *<br />     */<br />    public function appendFilterChain( & $outputBuffer )<br />    {<br />        // filter action of the common module that trims the html output<br />        $this->model->action( ''common'', ''filterTrim'', array(''str'' => & $outputBuffer) );        <br />    }<br /><br />    /**<br />     * init some variables<br />     *<br />     */    <br />    private function initVars()<br />    {<br />        // template array variables<br />        $this->tplVar[''node'']       = array();<br />        $this->tplVar[''childNodes''] = array();<br />        $this->tplVar[''nodeBranch''] = array();<br />        <br />        // template var with charset used for the html pages<br />        $this->tplVar[''charset''] = & $this->config[''charset''];<br />    }<br />}<br /> </pre>     <p>In bigger projects there is may a more closer focus on both filter methods. It would be usefull to develop a modul to manage filter actions for every public view. That means in every public filter methods you will find only one action that contacts the filter manager which do the real job.</p> <p>Example of the content of a prependFilterChain() methode would be some thing similar like:</p>     <pre>$this->model->action(''filtermanager'', ''runPrependFilters'',array(''view'' => ''xxx'')); </pre>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (5, 20, 4, 4, 'en', '2005-09-01 16:29:00', '2005-09-01 16:29:33', '2006-01-24 22:03:19', 0, 0, 'The Template', '', '', '', '', '<p>The template is a file in which you can define how the data assigned by the related view class is represented. This can be as an html page, pdf page, simple text page, downloads, xml or what ever. Smart3 use by default php as template language. The use of other templates engines is possible.</p> <p>You can find public templates in the <strong>/templates_xxx</strong> folder. It is possible to use more template folders to check different layouts. You can define in the global configuration array which template folder is active. The file name of a template must follow the convention:</p>   <pre>tpl.[Template name].php </pre>   <p>You can use the following variables in templates:</p>   <ul> <li><strong>$tpl</strong> - In this array you can find all variables assigned by the related view class.</li><li><strong>$viewLoader->xxx()</strong> - With this methode you can include an other view with name ''xxx'' from within a template. <br />   </li><li><strong>php functions - </strong>You can use all php functions except if it is restricted to some selected functions by the tokenanalyzer. See <a title="Smart Konfiguration" href="index.php?id_node=20&id_article=6&view=article">configuration</a>.</li> </ul>   <p>Take a closer look to the function $viewLoader->xxx(). With this function you can include an other view from within a template. This is an alternative to the php function include() which should be disabled if there are more developers who are going to work on a project. <strong>xxx</strong> stays for the name of the view to include. <strong>ViewXxx.php.</strong></p>   <p>You have to learn to use php very carefull in templates. Include as less as possible procedural code in templates. Try to do such jobs in the related view classes. Common php functions and constructs that you can use are, if,else,elseif,endif,foreach, endforeach, echo, count a.s.o.</p> <h4>Smarty</h4>   <p>Smart3 use by default <a href="http://www.php.net" target="_blank">php</a>  as template engine. It does a perfect job. However as an alternative engine you have the choice to activate <a href="http://smarty.php.net" target="_blank">Smarty</a>. There are 2 ways to enable Smarty as template engine:</p>   <ol> <li>Set in the configuration file /config/my_config.php the variable as follows: <br /> $SmartConfig[''public_template_engine''] = ''SmartTplContainerSmarty'';<br /> Now the project use Smarty in all templates.</li><li>Set view class variable as follows:<br /> public $templateEngine = ''SmartTplContainerSmarty'';<br />Each view can have its own template renderer.</li> </ol> <p>The view class array $this->tplVars contains all templates variables. You can use the content of this array in Smarty templates as follows: {$tpl.xxx}. (Study the Smarty examples in the public view and template foders). But you can use directly the Smarty instance from within the view classes:<br />  <strong>$this->viewVar[''smarty'']</strong> contains the Smarty instance. Further informations about Smarty <a target="_blank" href="http://smarty.php.net">here</a>.</p><p> </p>   <p> </p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (6, 20, 4, 7, 'en', '2005-09-01 16:30:00', '2005-09-01 16:30:13', '2006-02-15 21:31:40', 0, 0, 'The Global Configuration Array', '', '', '', '', '<p>Smart3 use and needs a configuration array to work in a proper way and to gives the administrator or the modul programmer the possibility to finetune the framework.</p>   <p>There are 2 ways to change the configuration array or to add some values:</p>      <ol> <li>You will find in the root folder <strong>/config</strong> a file <strong>my_config_dist.php. </strong>You have to rename this file in <strong>my_config.php</strong>. Here you can overwrite the default settings.</li><li>Some modules overwrite or provide an admin interface to overwrite some configuration settings.</li> </ol>   <p>The order in which Smart3 fetch the configuration array values is:</p>    <ol> <li>First it loads the default array values, <br />   </li><li>then it loads the array values in my_config.php if it is present,<br />   </li><li>then the modules can overwrite some variables. </li> </ol>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (7, 20, 4, 9, 'en', '2005-09-01 16:31:00', '2005-09-01 16:31:10', '2006-02-18 15:27:45', 0, 0, 'Roadmap', '', '', '', '', '<p>First we have to bring Smart3 out of the beta stage. There are some problem to solve and one among those is this of the PHPCodeAnalyzer class.</p> <p>We need one or more web page designers who help us to rewrite the admin layout (css) and to check its usability.</p> <p>Currently we use the tiny mice html editor. The plan is to include pear''s text_wiki as an alternative. So text_wiki specialists are welcome.</p> <p>Furthermore there are some modules in a planning stage:</p>   <ul> <li><strike><strong>Articles modul</strong></strike><strong> </strong>done<br />    </li><li><strike><strong>Links modul</strong></strike><strong> </strong>done<br />    </li><li><strong><strike>Keywords modu</strike>l </strong>done<strong><br /></strong></li><li><strong>Filter management modul</strong></li><li><strong>Full integration of web2 features (<strike>Ajax</strike>, <strike>RPC</strike>, <strike>RSS</strike>, Atom)</strong></li><li><strong>Switch from table to css layout<br /></strong></li> </ul>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (8, 20, 4, 10, 'en', '2005-09-01 16:32:00', '2005-09-01 16:32:30', '2006-02-15 21:31:31', 0, 0, 'Contribute', '', '', '', '', 'Smart3 is looking for high motivated programmers, designers, editors and translators. If you have some time left you can contact me at: <a href="mailto:smart@open-publisher.net" target="_self" title="Contact Email">smart@open-publisher.net</a>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (9, 23, 4, 1, 'en', '2005-09-01 16:36:00', '2005-09-01 16:36:03', '2006-01-26 10:49:21', 0, 0, 'Actions', '', '', '', '', '<p>Here you will find the description and usage of a few (not all) common action classes which you can use in public view classes. There are a few more action which are used for the admin views.</p>   <p>The parameters which have always the same meanings:</p>   <ul> <li><div><strong>''id_node''</strong> - Navigtion node ID to which the action is related<br /> </div></li><li><div><strong>''result''</strong> - A reference to an defined array in that the class store the result. <br />Ex.: &$this->tplVar </div></li><li><div><strong>''fields''</strong> - Array with the names of the node database fields. The possible fields are: ''id_node'', ''id_parent'', ''id_sector'', ''id_view'', ''status'', ''rank'', ''title'', ''short_text'', ''body'', ''format'', ''logo'', ''media_folder''</div></li><li><div><strong>''status''</strong> - In this array you can define the status of the node to retrive. The first element define the relation. The following relations are allowed: ''<'',''>'',''='',''>='',''<=''. The second element define the status value. Allowed values are 1 or 2. Example: array(''>'',1). <strong>1 means inaktiv. 2 means activ</strong>.</div></li><li><div><strong>''order''</strong> - If the result contains more nodes you can define here the order of those nodes. The first element define the field on which the nodes are ordered. Allowed fields are <strong>''title'',''rank''</strong>. The second element define the order. Allowed values are <strong>''asc'',''desc''</strong>. Example: array(''title'',''asc''). Default order is: ''rank'' ''asc''.<br /><br /></div></li> </ul>   <p>An action class always validate the data passed in the array (the third argument). If the validation fails an action class produce an exception. It depends on the error configuration if an error is displaed or logged. You will fin log entries in the <strong>/logs</strong> folder. </p>   <p> </p>   <h5>getNode</h5>   <p><strong>Description:</strong> Fill an array with the content of a node<br /> </p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getNode'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array(),<br />                               ''fields''  => array() ));<br /> </pre>   <h5>getChilds</h5>   <p><strong>Description:</strong> Fill an array with the content of child nodes of a given node.</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getChilds'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''status''  => array,       // optional<br />                               ''order''   => array,       // optional<br />                               ''fields''  => array() ));<br /> </pre>   <h5>getBranch</h5>   <p><strong>Description:</strong> Fill an array with the content of the branch nodes of a given node<br /> </p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getBanch'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array() ));</pre>   <h5>getTree</h5>   <p><strong>Description:</strong> Fill an array with the content of the nodes of a (sub)tree of a given node. id_node=0 means the whole nodetree.<br /> </p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getTree'',<br />                         array(''id_node'' => integer, // optional<br />                               ''result''  => & array,<br />                               ''fields''  => array() ));<br />  </pre>   <h5>getAllThumbs</h5>   <p><strong>Description:</strong> Fill an array with the image thumbnails data of a given node<br /> </p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getAllThumbs'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_node'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',''size'') ));<br /> </pre>   <h5>getAllFiles</h5>   <p><strong>Description:</strong> Fill an array with files data of a given node.</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getAllFiles'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_node'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'')));<br /> </pre>   <h5>getPicture</h5>   <p><strong>Description:</strong> Fill an array with the data of a given node image</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getPicture'',<br />                         array(''id_pic'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_node'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',<br />                                                  ''size'',''media_folder'') ));<br /> </pre>   <h5>getFile</h5>   <p><strong>Description:</strong> Fill an array with the data of a given node file</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''navigation'', ''getFile'',<br />                         array(''id_file'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_node'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'',''media_folder'')));</pre> <h5>getNodeStatus</h5>             <p><strong>Description:</strong> return the status of a navigation node.<br />      </p>                  <p><strong>Usage:</strong></p>    <pre>$status = $model->action(''navigation'',''getNodeStatus'', <br />                          array(''id_node'' => int);  <br />    </pre> <p> </p><h5>getKeywordIds</h5>            <p><strong>Description:</strong> Return all keywordId''s that are related to a given node ID<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''navigation'', ''getKeywordIds'',<br />                 array(''result''     => &$array(),<br />                       ''id_node''     => int,<br />                       ''key_status'' => array(''>|<|=|>=|<=|!='',<br />                                             1|2) // keyword status - optional<br />                       ) );<br /></pre><p> </p><h5>fromKeyword</h5>            <p><strong>Description:</strong> Return all nodes that are related to some given keyword ID''s defined in an array<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''navigation'',''fromKeyword'',<br />                 array(''id_key_list'' => array( int, int,..,..,..),<br />                       ''result''      => & array,<br />                       ''status''      => array(''>|<|=|>=|<=|!='',<br />                                              1|2),         // node status - optional<br />                       ''key_status''  => array(''>|<|=|>=|<=|!='',<br />                                              1|2),         // keyword status - optional<br />                       ''exclude''     => array( integers ),  // exclude id_node''s optional<br />                       ''order''       => array(''id_node'',''status'',''rank''<br />                                              ''id_parent'',''id_sector'',<br />                                              ''title'', ''asc|desc''), // optional<br />                       ''disable_sql_cache'' => TRUE,  // optional <br />                       ''fields       => array(''id_node'',''status'',''rank''<br />                                              ''format'',''media_folder'',<br />                                              ''id_parent'',''id_sector'',<br />                                              ''title'',''short_text'',<br />                                              ''body'',''id_view'',''logo'') ));</pre>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (10, 25, 4, 1, 'en', '2005-09-01 16:37:00', '2005-09-01 16:37:13', '2006-01-26 10:58:52', 0, 0, 'Actions', '', '', '', '', '<p>Here you will find the description and usage of a few (not all) common action classes of the misc module which you can use in public view classes. There are a few more action which are used for the admin views.</p>     <p>Action call parameters that have always the same meanings:</p>     <ul> <li><div><strong>''id_text''</strong> - ID of the text to which the action is related<br /> </div></li><li><div><strong>''result''</strong> - A reference to an defined array in that the class store the result. Ex.: &$this->tplVar </div></li><li><div><strong>''fields''</strong> - Array with the names of the text database fields. The possible fields are: ''id_text'',''status'',''title'',''short_text'',''body'',''media_folder''</div></li><li><div><strong>''status''</strong> - In this array you can define the status condition a text must accomplish to be retrived. The first element define the relation. The following relations are allowed: ''<'',''>'',''='',''>='',''<=''. The second element define the status value. Allowed values are 1 or 2. Example: array(''>'',1). <strong>1 means inaktiv. 2 means activ</strong>.</div></li><li><div><strong>''order''</strong> - If the result contains more textes you can define here the condition of the order of those textes. The first element define the field on which the textes are ordered. Allowed fields are <strong>''title'',''status''</strong>. The second element define the order. Allowed values are <strong>''asc'',''desc''</strong>. Example: array(''title'',''asc''). Default order is: ''title'' ''asc''.<br /><br /></div></li> </ul>     <p>An action class always validate the data passed in the array (the third argument). If the validation fails an action class produce an exception. It depends on the error configuration if an error is displayed or logged. You will find the log entries in the <strong>/logs</strong> folder. </p>     <h5>getText</h5>   <p><strong>Description:</strong> Fill an array with the content of a text</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''misc'', ''getText'',<br />                     array(''id_text'' => integer,<br />                           ''result''  => & array(),<br />                           ''fields''  => array() ));</pre>   <h5>getTextes</h5>   <p><strong>Description:</strong> Fill an array with the content of one or more textes</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''misc'', ''getTextes'',<br />                     array(''ids''    => array(id_x,id_y,id_z,..,..,..),<br />                           ''result'' => & array(),<br />                           ''order''  => array(),<br />                           ''fields'' => array() ));</pre>   <h5>getAllThumbs</h5>   <p><strong>Description:</strong> Fill an array with the image thumbnails data of a given text</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''misc'', ''getAllThumbs'',<br />                         array(''id_text'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_text'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',''size'') ));<br /> </pre>   <h5>getAllFiles</h5>   <p><strong>Description:</strong> Fill an array with files data of a given text</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''misc'', ''getAllFiles'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_text'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'')));<br /> </pre>   <h5>getPicture</h5>   <p><strong>Description:</strong> Fill an array with the data of a given text image</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''misc'', ''getPicture'',<br />                         array(''id_pic'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_text'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',<br />                                                  ''size'',''media_folder'') ));<br /> </pre>   <h5>getFile</h5>   <p><strong>Description:</strong> Fill an array with the data of a given text file</p>   <p><strong>Usage:</strong></p>   <pre>$this->model->action(''misc'', ''getFile'',<br />                         array(''id_file'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_text'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'',''media_folder'')));</pre><p> </p><h5>getKeywordIds</h5>            <p><strong>Description:</strong> Return all keywords that are related to a given text ID<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''misc'', ''getKeywordIds'',<br />                 array(''result''     => &$array(),<br />                       ''id_text''    => int,<br />                       ''key_status'' => array(''>|<|=|>=|<=|!='',<br />                                             1|2) // keyword status - optional<br />                       ) );<br /></pre><p> </p><h5>fromKeyword</h5>            <p><strong>Description:</strong> Return all textes that are related to some given keyword ID''s defined in an array<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''misc'',''fromKeyword'',<br />                 array(''id_key_list'' => array( int, int,..,..,..),<br />                       ''result''      => & array,<br />                       ''status''      => array(''>|<|=|>=|<=|!='',1|2), // text status - optional<br />                       ''key_status''  => array(''>|<|=|>=|<=|!='',1|2), // keyword status - optional<br />                       ''exclude''     => array( integers ),           // exclude id_textes''s optional<br />                       ''order''   => array(''title'', ''asc|desc''), // optional<br />                       ''disable_sql_cache'' => TRUE,  // optional <br />                       ''fields   => array(''id_text'',''status'',<br />                                          ''title'',''description'',<br />                                          ''format'',''media_folder'') ));</pre><p> </p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (28, 20, 4, 5, 'en', '2005-10-17 14:14:00', '2005-10-17 14:14:02', '2006-01-24 22:03:17', 0, 0, 'The Controller', '', '', '', '', '<p>The controller is the first entry point of the framework that handles requests. It is responsible for that every thing is going on the right way. Depending of its conception the controller execute the following operations:<br />  </p>   <ul> <li>Send a broadcast init action to all modules<br />   </li><li>Build some base instance. Amongs others an instance of the model class<br />   </li><li>Determine the workflow in view classes. Means which methode is executed when.<br /> </li><li>If necessairy build an template instance and renders a template<br />   </li> </ul>   <p>You can build different controllers with different conceptions. At this time there are 3 controller types available:<br /> </p>   <ol> <li>Public Web Controller - <strong>index.php</strong> - It handles the request for a public web page<br /> </li><li>Admin Web Controller - <strong>admin.php</strong> - It handles the request for the admin web interface<br /> </li><li>CLI Controller - <strong>cli.php</strong> - This controller handles command line jobs</li><li>XML_RPC Controller - <strong>rpcserver.php</strong> - This controller handles remote procedure calls<br />   </li>  </ol> <h5>The Cli Controller </h5>     <p>With this controller it is also possible to build complex shell applications as it is the case with unix bash. Through cli.php you can call a specific view.</p> <p>You can call under *nix the example cli View in /views_cli/ViewCliTest.php as follows: <br /> </p>     <pre>/usr/bin/php ./cli.php -v cliTest [ -f id_node title ] [ -idn 0 ]<br />OR<br />/usr/bin/php ./cli.php --view cliTest [ --fields id_node title ] [ --id_node 0 ] </pre>     <p>A Cli view works the same way as the other views except that:</p>     <ul> <li>Caching is disabled <br />   </li><li>No template is rendered<br /> </li><li>There is no append filter methode</li> </ul> <p>Smart3 uses <a href="http://pear.php.net/package/Console_Getargs" target="_blank">Console_Getargs</a> as command line parser.</p> <h5>The XML_RPC Controller</h5>     <p>The file <strong>rpcserver.php</strong> in the root folder is a server which handles xml rpc requests.  <a target="_blank" href="http://www.xmlrpc.com">RPC</a> is a spec and a set of implementations that allow software running on disparate operating systems, running in different environments to make procedure calls over the Internet. </p>     <p>  Smart3 uses the PEAR <a target="_blank" href="http://pear.php.net/package/XML_RPC/">XML RPC</a> library<span class="surname">. You can find the views in the <strong>/views_rpcserver</strong> folder. Currently there is only one view to test. With this view it is possible to fetch the latest modified or published artikles from a website. You can test the server with the Rpc-Client in <a href="http://www.smart3.org/tests/XmlRpcClient.php" target="_blank">/tests/XmlRpcClient.php</a>.</span></p>   <h6>Notice: </h6>   <p>The cli and the rpc controllers are yet experimental. It laks of experience with such implementations. But it show you of what is possible in Smart3.</p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (11, 22, 4, 1, 'en', '2005-09-01 16:38:00', '2005-09-01 16:38:07', '2006-01-24 22:42:11', 0, 0, 'Modules License', '', '', '', '', '<p>The modules are distributed under the following license: </p>   <pre>    GNU GENERAL PUBLIC LICENSE<br />		       Version 2, June 1991<br /><br /> Copyright (C) 1989, 1991 Free Software Foundation, Inc.<br />                       51 Franklin St, Fifth Floor, Boston, MA  02110-1301  USA<br /> Everyone is permitted to copy and distribute verbatim copies<br /> of this license document, but changing it is not allowed.<br /><br />			    Preamble<br /><br />  The licenses for most software are designed to take away your<br />freedom to share and change it.  By contrast, the GNU General Public<br />License is intended to guarantee your freedom to share and change free<br />software--to make sure the software is free for all its users.  This<br />General Public License applies to most of the Free Software<br />Foundation''s software and to any other program whose authors commit to<br />using it.  (Some other Free Software Foundation software is covered by<br />the GNU Library General Public License instead.)  You can apply it to<br />your programs, too.<br /><br />  When we speak of free software, we are referring to freedom, not<br />price.  Our General Public Licenses are designed to make sure that you<br />have the freedom to distribute copies of free software (and charge for<br />this service if you wish), that you receive source code or can get it<br />if you want it, that you can change the software or use pieces of it<br />in new free programs; and that you know you can do these things.<br /><br />  To protect your rights, we need to make restrictions that forbid<br />anyone to deny you these rights or to ask you to surrender the rights.<br />These restrictions translate to certain responsibilities for you if you<br />distribute copies of the software, or if you modify it.<br /><br />  For example, if you distribute copies of such a program, whether<br />gratis or for a fee, you must give the recipients all the rights that<br />you have.  You must make sure that they, too, receive or can get the<br />source code.  And you must show them these terms so they know their<br />rights.<br /><br />  We protect your rights with two steps: (1) copyright the software, and<br />(2) offer you this license which gives you legal permission to copy,<br />distribute and/or modify the software.<br /><br />  Also, for each author''s protection and ours, we want to make certain<br />that everyone understands that there is no warranty for this free<br />software.  If the software is modified by someone else and passed on, we<br />want its recipients to know that what they have is not the original, so<br />that any problems introduced by others will not reflect on the original<br />authors'' reputations.<br /><br />  Finally, any free program is threatened constantly by software<br />patents.  We wish to avoid the danger that redistributors of a free<br />program will individually obtain patent licenses, in effect making the<br />program proprietary.  To prevent this, we have made it clear that any<br />patent must be licensed for everyone''s free use or not licensed at all.<br /><br />  The precise terms and conditions for copying, distribution and<br />modification follow.<br />INSERT INTO `smart_article_article` VALUES (12, 2, 4, 1, 'en', '2005-09-30 10:26:00', '2005-09-01 16:39:43', '2006-02-19 19:26:22', 0, 0, 'Installation', '', '', '', '', '<p>Die Installation des Frameworks mit den ausgelieferten Modulen sollte schnell und einfach durchzuf&uuml;hren sein. Die entpackte Dateien m&uuml;ssen in das Verzeichniss kopiert werden auf den der Webserver Zugriff hat. Es muss eine MySql 4.1 Datenbank f&uuml;r das Projekt erstellt werden.</p> <p>Folgende Ordner m&uuml;ssen von php Skripten beschreibbar sein:</p>   <ul> <li>/cache</li><li>/cache/smartyCompiled</li> <li>/config</li><li>/data</li><li>/data/article</li><li>/data/article/rss<br /></li> <li>/data/common</li><li>/data/common/captcha</li><li>/data/common/rss_cache</li><li>/data/common/tinymce_cache<br /></li><li>/data/misc</li><li>/data/navigation</li><li>/data/user<br />   </li><li>/logs<br />   </li> </ul>    <p>Danach Rufen sie admin.php im Wurzelordner auf und folgen den Installationsanweisungen. M&ouml;chten sie das System erst einmal testen sollten sie unter &quot;Insert sample content&quot; (Beispielinhalt) die Checkbox &quot;yes&quot; aktivieren. Der Beispielinhalt besteht aus der kompletten Smart3 Dokumentation.</p>   <p>Falls die Installation erfolgreich abgeschlossen wurde werden sie zum Login weitergeleitet wo Sie sich dann mit &quot;superuser&quot;, dem gerade erstellten Passwort und der Ziffernfolge im Bild einloggen k&ouml;nnen.</p>    <p>In den Smart3 Ordnern <strong>/views_smart</strong> und <strong>/templates_smart</strong> liegen ein paar vorinstallierte Views und den entsprechenden Templates die als Beispiel dazu dienen sich mit dem System vertraut zu machen und sofort loslegen zu k&ouml;nnen. Diese installierten Dateien wurden erstellt um eine klassisches Webprojekt zu realisieren und sind auf den Beispielinhalt abgestimmt. Diese k&ouml;nnen sie dann an ihre eigenen Bed&uuml;rfnisse anpassen. F&uuml;r eigene Projekte sollte sie jedoch einen neuen View- bzw Templateordner erstellen damit die Dateien nicht bei einem Smart3 Upgrade &uuml;berschrieben werden. </p> <p>/templates_meinProjekt</p> <p>/views_meinProjekt </p> Im Modul Options der Administration k&ouml;nnen sie dann diese beiden Ordner aktivieren.', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (13, 2, 4, 2, 'en', '2005-09-01 16:40:00', '2005-09-01 16:40:32', '2006-01-24 21:55:43', 0, 0, 'Die Action Klasse', '', '', '', '', '<p>Um das Einbinden und Erstellen der Instanz einer Actionklasse braucht man sich nicht zu kümmern. Actionklassen sind Teile des Models und darum werden sie auch über eine Instanz des Models aufgerufen:</p>   <pre>$this->model->action( ''modulname'', ''actionname'', array() ); </pre>   <p>Diese Instanz steht in Viewklassen und auch in Actionklassen selbst zur Verfügung. Die Model-Action-Methode führt zuerst die <strong>validate()</strong> Methode und wenn diese TRUE zurückgibt die <strong>perform()</strong> Methode einer Actionkalsse aus. Actionklassen liegen in dem jeweiligen Modulordner <strong>/actions</strong>. <br /> </p>   <h4>Datenvalidierung</h4>   <p>Actionklassen haben einen defensiven Charakter. D.h. Daten die der Actionklasse übergeben werden, sollten von dieser auf ihre Gültigkeit überprüft werden. Genau darin liegt der Zweck der validate() Methode einer Actionklasse die vom Model ausgeführt wird. </p>   <pre>public function validate( $data = FALSE ){}; </pre>   <p>Die $data Variable wird dieser Methode als Parameter übergeben. Diese Variable ist dieselbe wie der dritte Parameter mit der diese Actionklasse aufgerufen wurde und enhält alle Daten die für das Funktionieren nötig sind:</p>   <pre>$this-model->action(''modulename'' , ''actionname'', <strong>$data</strong> ); </pre>   <p>Die validate() Methode muss TRUE zurückgeben damit die Hauptmethode perform() ausgeführt wird in der die eigentliche Aufgabe einer Actionklasse ausgeführt wird.</p>    <h4>Design einer Actionklasse </h4>    <p>Eine Actionklasse wird immer von einer Klasse SmartAction abgeleitet. Der Klassenname muss folgender Konvention entsprechen:</p>   <p>Action[Modulname][Definitionsname dieser Klasse]</p>   <p>Die Datei die diese Klasse enthält muss mit dem selben Namen wie der Klassennamen abgespeichert werden und mit der zusätzlichen Endung .php im <strong>/actions</strong> Ordner des jeweiligen Moduls. Der Speicherort der folgenden Klasse wäre dann:<br /> modules/common/action/ActionCommonTest.php</p>   <p>Danach kann diese vom System benutzt werden. Sie braucht also nicht extras registriert zu werden.<br /> </p>    <pre>class ActionCommonTest extends SmartAction<br />{<br />    /**<br />     * perform<br />     *<br />     * @param mixed $data<br />     */<br />    public function perform( $data = FALSE )<br />    {<br />    }<br />    <br />    /**<br />     * Validate data passed to this action<br />     * @param mixed $data<br />     * @return bool<br />     */<br />    public function validate( $data = FALSE )<br />    {<br />        return TRUE;<br />    }    <br />} <br /></pre>   <p>Dieses Beispiel einer Actionklasse zeigt die Methoden die darin enthalten sein können. Falls sie diese Klasse jetzt abspeichern als common/actions/ActionCommonTest.php und nachfolgend aufrufen, passiert nichts, weil in der perform() Methode nichts ausgeführt wird.</p>   <pre>$this->model->action( ''common'', ''test'', array() ); </pre>   <p> Deswegen hier ein Beispiel einer Klasse die Leerzeichen bevor und hinter einer übergebenen Zeichenkette wegnimmt:<br /> </p>   <pre> class ActionCommonTrim extends SmartAction<br /> {<br />    /**<br />     * Trim a string<br />     *<br />     * @param mixed $data<br />     */<br />    public function perform( $data = FALSE )<br />    {<br />        $data[''result''] = trim( $data[''str''] ); <br />    }<br />    <br />    /**<br />     * Validate data passed to this action<br />     */<br />    public function validate( $data = FALSE )<br />    {<br />        if(!isset($data[''error'']))<br />        {<br />            throw new SmartModelException(''Missing "error" var''); <br />        }<br /><br />        if(!isset($data[''result'']))<br />        {<br />            $data[''error''] = "array ''result'' key is not defined!";<br />            return FALSE<br />        }<br /><br />        if(!isset($data[''str'']))<br />        {<br />            $data[''error''] = "array ''str'' key is not defined!";<br />            return FALSE;<br />        }<br /><br />        if(!is_string($data[''str'']))<br />        {<br />            $data[''error''] = "array ''str'' value is not type string!";<br />            return FALSE;<br />        }<br />        return TRUE;<br />    }    <br />}</pre>   <p>Aufgerufen wird diese Klasse mit:</p>   <pre>// init variables<br />$trimmed_string = '''';<br />$error = '''';<br /><br />$this->model->action( ''common'', ''trim'',<br />                      array(''result'' => & $trimmed_string,<br />                            ''str''    => ''  Dieses ist ein Test    '',<br />                            ''error''  => & $error)); <br /><br />if(!empty($error))<br />{<br />    echo $error;<br />} <br />else<br />{<br />    echo $trimmed_string;<br />} <br /></pre>   <p>Die meiste Arbeit besteht darin die validate() Methode zu erstellen. Weil hier alle Daten auf ihre Gültigkeit überprüft werden sollen, damit die perform() Methode die eigentliche Aufgabe, nähmlich eine Zeichenkette zu trimmen, ausführen kann. </p> <p>Es gib verschiedene Möglichkeiten was passieren soll wenn Daten ungültig sind. Im Falle wo eine saubere Programmausführung nicht gewährleistet werden kann sollte eine Exception erstellt werden. Das System fängt diese ab und erstellt ein Logeintrag und leitet zu einer Errorseite weiter auf der die Fehlermeldung erscheint. Dieses ist hier der Fall wenn eine error Variable nicht definiert wurde. Bei den anderen Validierungsmassnahmen wird das error Array mit den Fehlermeldungen geschrieben die sie dann selbst behandeln. Mit Ausnahme einer Exception sollte bei einem Validierungsfehler die Methode FALSE zurückgeben, damit perform() nicht ausgeführt wird. Andernfalls TRUE.</p> <p>Wichtig ist zu beachten, dass wenn eine Actionklasse Werte zurückgeben soll diese in einer Variable abgespeichert werden, die dem Daten-Array als Refernz zu übergeben werden muss.</p>   <h4>Klassenvariablen </h4>   <p>Die Model Instanz übergibt der Actionklasse folgende Klassen variablen:</p>   <ul> <li>$this->model - Instanz der Modelklasse. Wird unter anderem dazu benötigt eine weitere Action auszuführen.<br /> </li><li>$this->config - Globales Konfigurationsarray.</li><li>$this->constructorData - Daten die an den Actionklassen-Contructor übergeben wurde, was eher selten der Fall sein dürfte.<br />   </li> </ul>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (14, 2, 4, 4, 'en', '2005-09-01 16:41:00', '2005-09-01 16:41:12', '2006-01-24 21:55:40', 0, 0, 'Die Template (Vorlage)', '', '', '', '', '<p>Eine Template (Vorlage) ist nichts weiter als eine Datei in der festgelegt wird wie die Daten die von der Viewklasse aufbereitet wurden dargestellt werden sollen. Dieses kann in From einer HTML, PDF, XML, Text oder was auch immer sein. Smart3 wird mit einem PHP Templateparser ausgestatten. In den Templates kommt also php zum Einsatz. Es ist jedoch auch möglich eine andere Templateengine zu benutzen. Dazu später mehr. </p> <p>Templates für Projekte liegen im <strong>/templates_xxx</strong> Ordner. Es ist somit möglich mehrere Templateordner zu betreiben in denen z.B. für ein Webprojekt verschieden Layouts ausgetestet werden können. Der aktive Templateordner wird im globalen Konfigurationsarray festgelegt. Der Name der Templatedatei muss folgender Konvention entsprechen:</p>   <pre>tpl.[Template Name].php </pre>   <p>In Templates stehen, wenn der in Smart3 verwendete php Templateparser benutzt wird, folgende Variablen und Funktionen zur verfügung:</p>   <ul> <li><strong>$tpl</strong> - In diesem Array stehen alle Daten die von der entsprechenden Viewklasse aufbereitet wurde.</li><li><strong>$viewLoader->xxx()</strong> - Einbinden einer View mit Namen ''xxx'' <br />   </li><li><strong>php Funktionen - </strong>Dieses ist aus Sicherheitsgründen nicht immer wünschenswert. Deshalb wurde in Smart3 ein Tokenanalyzer integriert, der, wenn er aktiv ist, nur ganz bestimmte im Konfigurationsarray festgelegte php Funktionen zuläst. Siehe <a href="index.php?id_node=2&id_article=16&view=article" title="Smart Konfiguration">Konfiguration</a>.</li> </ul> <p> Einer näheren Betrachtung bedarf die Funktion $viewLoader->xxx(). Durch diese Funktion wird von innerhalb einer Template eine andere View und ihre dazugehörige Template eingebunden. Diese Funktion steht als Alternative zu include(), die aus Sicherheitsgründen nicht empfehlenswert ist. <strong>xxx</strong> steht für den Namen der View die eingebunden werden soll. Der Name muss also exakt mit der View übereinstimmen damit es funktioniert. Also <strong>ViewXxx.php.</strong></p> <p>Man sollte es sich zum Prinzip machen so wenig wie möglich prozeduraler Code in die Templates reinzuschreiben. Dieser sollte wenn möglich in die Viewklassen verlegt werden. Also wenn Daten irgenwelche Textfilter durchlaufen müssen, etwa um email Adressen für Spamboots unkenntlich zu machen, so sollte dieses in der entsprechenden Viewklasse passieren. Es ist allerdings klar, das man in Templates nicht um if,else,endif,foreach, endforeach, echo herumkommt. Viel mehr sollte es allerdings nicht sein. Templates sind kein Tummelplatz für phpProgrammierer, sondern Designer müssen damit klarkommen. Darum muss der ViewProgrammierer dem Templatedesigner Klarheit darüber geben welche Daten und in welcher From ihm zur Verfügung stehen.</p>   <h4>Ich möchte eine andere Template Engine benutzten<br /> </h4>   <p>Smart3 kommt mit einem php Template Renderer. Dieser besteht aus einer Klasse SmartTplContainerPhp, die in der Datei /smart/includes/SmartTplContainerPhp.php liegt. Im globalen Konfigurationsarray wird festgelegt, dass diese Klasse für das Renderen von Templates benutzt wird. </p>   <pre> $SmartConfig[''public_template_engine''] = ''SmartTplContainerPhp'';</pre><p> Um eine andere Klasse zu benutzen muss dieses im Konfigurationsarray umgeänder werden. Und diese Klasse muss sich natürlich im Ordner smart/includes befinden.</p><p>Als Neuerung wurde Smarty als Alternativengine hinzugefügt. </p> <h4>Smarty</h4> <p>Es gibt 2 Möglichkeiten um Smarty als Templateengine zu aktivieren:</p> <ol>   <li>In der Konfigurationsdatei /config/my_config.php die Variable  auf <br /> $SmartConfig[''public_template_engine''] = ''SmartTplContainerSmarty'';<br /> zu setzen. Hierbei wird bei allen Templates Smarty benutzt.</li>   <li>In der Viewklasse folgende Klassenvariable zu setzen:<br /> public $templateEngine = ''SmartTplContainerSmarty'';<br /> Somit kann man individuel für jede View die Templateengine setzen.</li> </ol> <p>Die in der Viewklasse im Array $this->tplVars enthaltenen Variablen werden somit vom Smarty Container automatisch übernommen. In den Templates steht dieses Array dann folgen zur Verfügung {$tpl.xxx}. (Als Beispiel sollten sie die Smarty Views und Templates betrachten.) In den Viewklassen ändert sich somit fast nichts gegenüber wenn php als Templateengine benutzt wird. Es sollte jedoch auch möglich sein innerhalb der Viewklassen direkt über die Smartyinstanz Templatevariablen zu setzen.<br /> <strong>$this->viewVar[''smarty'']</strong> enthält die Smartyinstanz. Weiter Infos zu Smarty erhalten sie direkt <a target="_blank" href="http://smarty.php.net">von dieser Webseite</a> .</p> <h5>Hinweis </h5> <p>Falls sie Smarty einsetzen wollen, sollten sie im globalen Konfigurationsarray "error_reporting" auf E_ALL herrunterstellen. Smarty ist php4 Code und veranlasst das System unter E_STRICT viele Meldungen zu erstellen.<br /> </p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (15, 2, 4, 3, 'en', '2005-09-01 16:41:00', '2005-09-01 16:41:51', '2006-01-24 21:55:42', 0, 0, 'Die View Klasse', '', '', '', '', '<p>In dieser Klasse wird zum grossen Teil festgelegt wie eine Anfrage an das System beantwortet wird. Im klassischen Fall einer Webseite wäre das die Daten zu sammeln die für die Darstellung einer angefragten Webseite benötigt werden. Um das Einbinden und das Erstellen einer Instanz einer Viewklsse braucht man sich nicht zu kümmern. Der Controller übernimmt das alles. Anschliessend führt der Controller die Methoden dieser Klasse in einer bestimmten Reihenfolge aus. Diese Reihenfolge ist:</p>   <ul> <li><strong> auth()</strong> - Falls nötig kann hier ein Authentifizierungs-Mechanismus eingebaut werden.</li><li><strong>prependFilterChain()</strong> - Hier kann Code ausgeführt werden bevor der eigentlichen Hauptmethode. Z.B. http Headers senden, Spamboots abfangen usw.</li><li><strong>perform() </strong>- Die eigentliche Hauptmethode.</li><li><strong>appendFilterChain( & $tplBufferContent )</strong> - Diese Methode wird ausgeführt nachdem eine Template gerendert wurde. $tplBufferContent enthält den Inhalt einer gerenderten Template. Eine Mögliche Anwendung wäre Kommentare aus Templates zu entfernen.</li> </ul> <p>Deweiteren gibt es folgende Klassenvariablen die man sich merken sollte:</p>   <ul> <li><strong> $tplVar</strong> - Dies ist das Array das sämtliche Datenvariablen enthält die in der Template eingefügt werden können. Ex.: $this->tplVar[''title''] = ''Hello World''; steht in der Template als $tpl[''title''] zur Verfügung.</li><li><strong>$viewVar</strong> - In diesem Array können alle Daten abgelegt werden die in einer View oder mehreren Views verwendet werden. Dieses ist sehr nützlich bei Daten die in allen verschachtelten Views zur Verfügung stehen müssen.</li><li><strong>$model</strong> - Die Instanz der Model Klasse. Wird benötigt um eine Action auszuführen. Ex.: $this->model->action(''common'',''test'');</li><li><strong>$session</strong> - Die Instanz der Session Klasse</li><li><strong>$config</strong> - Dieses Array enthält sämtliche Konfigurationsdaten.</li><li><strong>$template</strong> - Dieser String enthält den Name der Template die mit dieser View verknüpft ist. Im Prinzip geht der Controller davon aus, dass die Template den selben Name hat wie die Viewklasse. Ist immer unbedingt anzugeben wenn die Template einen anderen Name hat.</li><li><strong>$renderTemplate</strong> - Ist vom Type BOOL. Steht diese aus FALSE wird keine Template gerendert.</li><li><strong>$templateEngine</strong> - Für jede View kann individuell die Templateengine festgelegt werden. Zur Zeit gibt es 2 Engines: ''SmartTplContainerPhp'' (Voreinstellung) und ''SmartTplContainerSmarty'' <br />   </li> <li><strong>$templateFolder</strong> - Hier kann man den Pfad zum Templateordner angeben wenn dieser eine Anderer sein soll wie im Konfigurationsarray angegeben.</li><li><strong>$viewData</strong> - Es kann vorkommen, dass einer View Instanz vom Controller Daten übergeben werden. Diese stehen dann in dieser Variable.</li><li><strong>$cacheExpire</strong> - Bestimmt die Zeit in Sekunden wo die Template zwischengespeichert (gecacht) wird. 0 bedeutet, dass die Template nicht gecacht werden soll.</li><li><strong>$cacheId</strong> - ID der Cachedatei. Wird normalerweise vom System erstellt. <br />   </li>  </ul> <p>View Klassen die zum erstellen eines Webprojektes benötig werden stehen im Wurzelordner <strong>/views_xxx</strong> . Wobei xxx ein beliebiger Name sein kann. Es ist somit möglich mehere View Ordner paralell zu betreiben. Das gobale Konfigurationsarray enhält den Ordnername der gerade aktiv ist.</p>   <h5>Beispiel einer ViewKlasse.</h5>   <p>Diese Beispiel zeigt einen Auszug aus der Viewklasse in ViewNode.php. Also es geht hier darum die Daten des angefragten Navigationsknoten zu erhalten, ebenso wie die Daten der Unterknoten (child), der Knotenast (wie bei yahoo) und entsprechende Templatevariablen mit diesen Daten zu beschreiben. Besonders beachtet sollte man die prependFilterChain() Methode. Hier wird geprüft ob die id_node, also die Navigationsknoten ID nur Ziffern enthält. Falls nicht wird die Error Template gerendert.</p>   <pre>class ViewNode extends SmartView<br />{<br />    /**<br />     * Cache expire time in seconds for this view<br />     * 0 = cache disabled<br />     */<br />    public $cacheExpire = 3600;<br />    <br />    /**<br />     * Execute the view of the "node" template<br />     */<br />    function perform()<br />    { <br />        // init variables (see private function below)<br />        $this->initVars();<br />          <br />        // get requested node content<br />        $this->model->action(''navigation'',''getNode'', <br />                             array(''result''  => & $this->tplVar[''node''],<br />                                   ''id_node'' => $this->current_id_node,<br />                                   ''fields''  => array(''title'',''body'',''id_node'',''media_folder'')));<br /><br />        // get child nodes content of the requested node<br />        // only with status=2, means active      <br />        $this->model->action(''navigation'',''getChilds'', <br />                             array(''result''  => & $this->tplVar[''childNodes''],<br />                                   ''id_node'' => $this->current_id_node,<br />                                   ''status''  => array(''='',2),<br />                                   ''fields''  => array(''title'',''short_text'',''id_node'')));<br /> <br />        // get navigation node branch content of the requested node<br />        $this->model->action(''navigation'',''getBranch'', <br />                             array(''result''  => & $this->tplVar[''nodeBranch''],<br />                                   ''id_node'' => $this->current_id_node,<br />                                   ''fields''  => array(''title'',''id_node'')));                   <br />    }<br /><br />    /**<br />     * authentication<br />     *<br />     */<br />    public function auth()<br />    {<br />    }<br /><br />    /**<br />     * prepend filter chain<br />     *<br />     */<br />    public function prependFilterChain()<br />    {<br />        // fetch the current id_node. If no id_node defined or not numeric<br />        // this view class loads the error template<br />        if( !isset($_REQUEST[''id_node'']) || preg_match("/[^0-9]+/",$_REQUEST[''id_node'']) ) <br />        {<br />            $this->template  = ''error'';     <br />        }<br />        else<br />        {<br />            $this->current_id_node    = (int)$_REQUEST[''id_node''];          <br />        }<br />        <br />        // filter action of the common module to prevent browser caching<br />        $this->model->action( ''common'', ''filterDisableBrowserCache'');    <br />    }<br /><br />    /**<br />     * append filter chain<br />     *<br />     */<br />    public function appendFilterChain( & $outputBuffer )<br />    {<br />        // filter action of the common module that trims the html output<br />        $this->model->action( ''common'', ''filterTrim'', array(''str'' => & $outputBuffer) );        <br />    }<br /><br />    /**<br />     * init some variables<br />     *<br />     */    <br />    private function initVars()<br />    {<br />        // template array variables<br />        $this->tplVar[''node'']       = array();<br />        $this->tplVar[''childNodes''] = array();<br />        $this->tplVar[''nodeBranch''] = array();<br />        <br />        // template var with charset used for the html pages<br />        $this->tplVar[''charset''] = & $this->config[''charset''];<br />    }<br />}<br /> </pre>   <p>Vor allem bei grösseren Projekten kommt den zwei Filter Methoden grössere Bedeutung zu. So ist es vorstellbar ein Modul zu entwickeln das Filtereinstellungen jeder Viewklasse verwalten könnte. D.h. in jeder Filtermethode einer Viewklasse wären dann jeweils nur ein Actionaufruf. Beispiel für den Inhalt einer prependFilterChain() Methode wäre dann:</p>   <pre>$this->model->action(''filtermanager'', ''runPrependFilters'',array(''view'' => ''xxx'')); </pre>   <p>Mit Sicherheit wird in nächster Zeit ein solches Modul kommen. </p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (16, 2, 4, 7, 'en', '2005-09-01 16:42:00', '2005-09-01 16:42:41', '2006-02-19 18:13:24', 0, 0, 'Konfiguration', '', '', '', '', '<p>Smart3 greift auf ein php Konfigurationsarray zur&uuml;ck. Dieses wird dazu ben&ouml;tigt um intern korrekt zu funktionieren und um dem Administrator bzw Modulprogrammierer eines Projektes die M&ouml;glichkeit zu geben auf das Verhalten des Systems Einfluss zu nehmen.</p> <p>Es gibt zwei M&ouml;glichkeiten die Konfigurationsvariablen zu ver&auml;ndern:</p>    <ol> <li>Im Wurzel-Ordner <strong>/config</strong> steht die Datei <strong>my_config_dist.php. </strong>Diese Datei muss in <strong>my_config.php</strong> umbenannt werden. Darin kann man die Default-Konfiguration von Smart3 &uuml;berschreiben indem man den darin enthaltenen Variablen andere Werte zuweist.</li><li>Indem man Module entwickelt die das Konfigurationsarray &uuml;berschreiben. Einige Module &uuml;berschreiben Konfigurationsvariablen.<br /> </li> </ol>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (17, 2, 4, 8, 'en', '2005-09-01 16:43:00', '2005-09-01 16:43:29', '2006-02-11 14:36:32', 0, 0, 'Die Smart3 Projekt Views und Vorlagen', '', '', '', '', '<p>In den Wurzelordnern /templates_smart und /views_smart stehen View Klassen bzw ihre entsprechenden Templates die auf ein klassisches Webprojekt zugeschnitten sind. Diese können sie als Vorlage benutzten um ihre eigenen Projekte zu erstellen. Sie sollten allerdings ihre Projektviews bzw Templates in Ordnern mit neuem Namen abspeichern. Z.B. /views_myproject /templates_myproject. Ansonsten werden beim nächsten Update ihre veränderten Dateien überschrieben. <br />  </p> <p>Es sind folgende Views und Templates vorhanden in alphabetischer Reihenfolge:</p>   <ul> <li><strong>ViewArticle.php</strong> bzw <strong>tpl.article.php</strong> -  Hier werden die Daten des angefragten Artikels erstellt bzw in der korrespondierenden Vorlage angezeigt.</li><li><strong>ViewError.php</strong> bzw <strong>tpl.error.php</strong> -  Steht im Konfiguartionsarray die Fehlerbehandlung auf Schow, so werden hierüber Fehlermeldungen ausgegeben.</li> <li><strong>ViewHeader.php</strong> bzw <strong>tpl.header.php</strong> - Hier wird der Kopf zentral für jede Webseite erstellt. Diese View wird von innerhalb jeder anderen Template die den Kopf benötigt eingebunden.<br /> </li><li><strong>ViewIndex.php</strong> bzw <strong>tpl.index.php</strong> - Daten zur Eingangsseite und ihr Aussehen werden hier bestimmt.</li><li><strong>ViewLogout.php</strong> - Hat sich ein Webbesucher vorher über das Administrationsinterface eingelogt so erscheint auf der öffentlichen Webseite ein Link um sich auszuloggen. Über diesen Link wir die Logout View aufgerufen die die entsprechende Prozedur ausführt. Diese View benötigt keine Template.</li><li> <strong>ViewMainNavigation.php</strong> bzw <strong>tpl.mainNavigation.php</strong> - Hier werden die Hauptnavigations Links der rechten Spalte zentral für jede Webseite erstellt. Jede andere Template die diese Links benötigt bindet diese an entsprechender Stelle ein.<br /> </li><li><strong>ViewNode.php</strong> bzw <strong>tpl.node.php</strong> - Hier werden die Daten zu dem erfragten Navigationsknoden und seinen Unterknoten gesammelt und dargestellt. Fast jede Webseite (also Knoten) des ganzen Webprojektes wird über diese View dargestellt.</li><li><strong>ViewPicture.php</strong> bzw <strong>tpl.picture.php</strong> - Enthält ein Text ein Thumbnail-Bild und klickt man darauf so wird diese View aktiviert die das Bild in ganzer Grösse darstellt.</li><li><strong>ViewSearch.php</strong> bzw <strong>tpl.search.php - </strong>Hier werden alle Artikeltitel mit den jeweiligen Navigationsknoten angezeigt die einem Suchkriterium entsprechen.<br />   </li> <li><strong>ViewSimpleNode.php</strong> bzw <strong>tpl.simpleNode.php</strong> - Für Knoten die keine Unterknoten enthalten, also wo nur der darin enthaltener Text dargestellt werden soll benötigen diese View.</li><li><strong>ViewSitemap.php</strong> bzw <strong>tpl.sitemap.php</strong> - Hier werden alle Naviagtionsknoten hierarschisch dargestellt.</li> </ul>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (18, 2, 4, 9, 'en', '2005-09-01 16:44:00', '2005-09-01 16:44:13', '2006-03-05 21:19:01', 0, 0, 'Roadmap', '', '', '', '', ' <p>Erstmal muss Smart3 ausgiebig getestet werden und gegebenfalls notwendigen &Auml;nderungen unterworfen werden. Also stabil und zuverl&auml;ssig.   </p>     <p>Bei den Modulen sollte zudem das Layout &uuml;berarbeitet werden bzw die Benutzerfreundlichkeit. Dieses &uuml;berlasse ich gerne jenen die mehr davon verstehen als ich selbst.</p>     <p>Insofern ben&ouml;tigt kommt <a href="http://tinymce.moxiecode.com/" target="_blank" title="Tiny Mice HTML Editor">TinyMice</a> als Editor zum Einsatz. Als Alternative ist PEAR <a href="http://pear.php.net/package/Text_Wiki/" target="_blank">text_wiki</a>  geplant. Beide L&ouml;sungen haben ihre Vor- und Nachteile. Einen Idealweg gibt es nicht. Tiny Mice hat den Vorteil, dass er benutzerfreundlich ist. Der Nachteil ist, dass man html Code in der Datenbank hat, was Schwierigkeiten bereitet wenn man die Daten weiterverarbeiten m&ouml;chte. Pear Text_wiki ist eben nicht so benutzerfreundlich weil man Layoutver&auml;nderungen am Text nicht sehen kann und zudem kryptische Anweisungen in den Text schreiben muss. Der Vorteil ist, dass sich die Daten im nachhinein besser weiterverarbeiten lassen.<br />  </p>     <p>In Planung sind folgende Module:</p>       <ul>  <li><strike><strong>Artikel Modul</strong></strike> -  wobei jeder Artikel einem Navigationsknoten untergeordnet werden kann (done)<br />     </li> <li><strike><strong>Links Modul</strong></strike> - gleiches wie oben (done)<br />     </li> <li><strike><strong>Schl&uuml;sselwort Modul</strong></strike> - Durch dieses wird es m&ouml;glich Elemente horrizontal untereinander zu verbinden. Es lassen sich so komplexe Zusammenh&auml;nge herstellen und darstellen.</li> <li><strong>Filter Modul</strong> - Hier geht es um die Administration von FilterActions die in den unterschiedlichen ViewKlassen zum Einsatz kommen.</li>  </ul><p>&nbsp;</p><p>Die Module werden w&auml;hrend den folgenden Versionen <strong>Web2</strong> f&auml;hig gemacht. D.h. u.a Integration von <strong>Ajax</strong>, <strong>Rss</strong>, <strong>Atom</strong> usw. </p><p>Desweiteren wird eine multilanguage Unterst&uuml;tzung angestrebt. D.h. Ber&uuml;cksichtigung der Schreibrichtung (z.b. recht-links f&uuml;r arabisch) sowie &Uuml;bersetzung des Administrationsinterface.</p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (19, 2, 4, 10, 'en', '2005-09-01 16:44:00', '2005-09-01 16:44:46', '2006-02-11 14:36:21', 0, 0, 'Mitmachen', '', '', '', '', '<p>Zur Zeit arbeitet nur ein Einziger am Smart3 Projekt. Wer Zeit, Lust, Energie und das nötige KnowHow hat am Projekt mitzuarbeiten sollte sich über diese Email melden: <a title="Mitmachen Email" target="_self" href="mailto:smart@open-publisher.net">smart@open-publisher.net</a></p> <p>Gefragt sind Leute die sich in mindestens einem der folgenden Fachgebiete auskennen:<br />  </p>   <ul> <li>Webseitendesign (Umsetzung) für die Administrationsoberfläche<br />   </li><li>Webseiten Usability<br />   </li><li>Gute PHP5 Kentnisse.</li><li>Gute MySql Kentnisse. Am Besten mit der Version 4.1</li><li>Redaktionelle Überarbeitung der Dokumentation bzw Übersetzung ins Englische, Französische<br />   </li><li>Geduld, und den nötigen Willen das Projekt vorran zu bringen.<br />   </li> </ul><p>  Ziel ist es die bestehenden Module auszubauen bzw Neue zu erstellen.</p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (20, 9, 4, 1, 'en', '2005-09-01 16:45:00', '2005-09-01 16:45:39', '2006-01-26 10:38:54', 0, 0, 'Actions', '', '', '', '', '<p>Hier befindet sich eine Liste der Actionklassen des Navigationsmodul die zum erstellen der Views benutzt werden können. Es gibt noch eine Reihe weiterer Klassen, die aber nur für die Views im Administrationsbereich verwendung finden.</p> <p>Die Parameter der Actionaufrufe haben immer die selbe Bedeutung. Erklärung folgender Parameter:</p> <ul> <li><div><strong>''id_node''</strong> - Navigationsknotenpunkt ID auf die sich die Action bezieht</div></li><li><div><strong>''result''</strong> - Hier muss die Referenz eines vorher definierten leeren Arrays übergeben werden. <br />Ex.: &$this->tplVar </div></li><li><div><strong>''fields''</strong> - Array mit den Namen der Knoten Datenbank-Felder. Die möglichen Felder eines Knoten sind: ''id_node'', ''id_parent'', ''id_sector'', ''id_view'', ''status'', ''rank'', ''title'', ''short_text'', ''body'', ''format'', ''logo'', ''media_folder''</div></li><li><div><strong>''status''</strong> - In diesem array wird definiert welchen Status der oder die Knoten haben müssen. Das erste Element im Array definiert die Beziehung. Eine von den folgenden sind möglich: ''<'',''>'',''='',''>='',''<=''. Das zweite element definiert den Statuswert. entweder 1 oder 2. Beispiel: array(''>'',1). Der oder die Knoten müssen einen Status grösser als 1 haben. <strong>1 bedeutet inaktiv. 2 bedeutet activ</strong>.</div></li><li><div><strong>''order''</strong> - Kann das Resultat aus meheren Knoten bestehen so wird in einem Array festgelegt wie die Reihenfolge des Resultates sortiert wird. Das erste Element im array definiert nach welchem Feld sortiert wird. Mögliche Felder sind <strong>''title'',''rank''</strong>. Das zweite Element definiert die Reihenfolge. Mögliche Werte sind <strong>''asc'',''desc''</strong>. Also für aufsteigend und absteigend. Beispiel: array(''title'',''asc''). Wird order nicht angegeben so wird nach ''rank'' ''asc'' das Resultat sortiert.<br /><br /></div></li> </ul> <p>Eine Actionklasse prüft immer die Parameter auf ihre Gültigkeit. Sollten Parameter nicht das richtige Format haben oder Parameter die fehlen, so erstellt die Klasse eine Exception. D.h. der Programablauf wird umgeleietet. Entweder wird eine Fehlermeldung angezeigt. Oder es nur ein Logeintrag geschrieben im Wurzelordner <strong>/logs. </strong>Es hängt davon ab wie die Konfigurationsvariablen für die Fehlerbehandlungengesetzt sind. </p> <p> </p> <h5>getNode</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit dem Inhalt eines Knotens</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getNode'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array(),<br />                               ''fields''  => array() ));<br /> </pre> <h5>getChilds</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit dem Inhalt der Kindsknoten eines Knotens</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getChilds'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''status''  => array,       // optional<br />                               ''order''   => array,       // optional<br />                               ''fields''  => array() ));<br /> </pre> <h5>getBranch</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit dem Inhalt jedem Knoten de Baumastes auf dem der Knoten sich befindet</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getBanch'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array() ));</pre> <h5>getTree</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit dem Inhalt des Knotenbaumes. Wird id_node angegeben so wird der Baum ab diesem Konten zurückgegeben.</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getTree'',<br />                         array(''id_node'' => integer, // optional<br />                               ''result''  => & array,<br />                               ''fields''  => array() ));<br />  </pre> <h5>getAllThumbs</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Bilderdaten eines Knotens</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getAllThumbs'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_node'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',''size'') ));<br /> </pre> <h5>getAllFiles</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Dateidaten eines Knoten</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getAllFiles'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_node'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'')));<br /> </pre> <h5>getPicture</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten eines Bildes</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getPicture'',<br />                         array(''id_pic'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_node'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',<br />                                                  ''size'',''media_folder'') ));<br /> </pre> <h5>getFile</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten einer Datei</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''navigation'', ''getFile'',<br />                         array(''id_file'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_node'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'',''media_folder'')));</pre> <h5>getNodeStatus</h5>              <p><strong>Beschreibung:</strong> Gibt den Status eines Knotens zurück.<br />      </p>                   <p><strong>Syntax:</strong></p>     <pre>$status = $model->action(''navigation'',''getNodeStatus'', <br />                         array(''id_node'' => int);  </pre> <p> </p><h5>getKeywordIds</h5>            <p><strong>Beschreibung:</strong> Gibt alle SchlüsselwortID''s zurück die mit einem NodeID verknüpft sind<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''navigation'', ''getKeywordIds'',<br />                 array(''result''     => &$array(),<br />                       ''id_node''     => int,<br />                       ''key_status'' => array(''>|<|=|>=|<=|!='',<br />                                             1|2) // keyword status - optional<br />                       ) );<br /></pre><p> </p><h5>fromKeyword</h5>            <p><strong>Beschreibung:</strong> Gibt alle Nodes, die mit den Schlüsselwort-Id''s die in einem Array definiert sind, zurück<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''navigation'',''fromKeyword'',<br />                 array(''id_key_list'' => array( int, int,..,..,..),<br />                       ''result''      => & array,<br />                       ''status''      => array(''>|<|=|>=|<=|!='',<br />                                              1|2),         // node status - optional<br />                       ''key_status''  => array(''>|<|=|>=|<=|!='',<br />                                              1|2),         // keyword status - optional<br />                       ''exclude''     => array( integers ),  // exclude id_node''s optional<br />                       ''order''       => array(''id_node'',''status'',''rank''<br />                                              ''id_parent'',''id_sector'',<br />                                              ''title'', ''asc|desc''), // optional<br />                       ''disable_sql_cache'' => TRUE,  // optional <br />                       ''fields       => array(''id_node'',''status'',''rank''<br />                                              ''format'',''media_folder'',<br />                                              ''id_parent'',''id_sector'',<br />                                              ''title'',''short_text'',<br />                                              ''body'',''id_view'',''logo'') ));</pre> ', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (21, 12, 4, 1, 'en', '2005-09-01 16:46:00', '2005-09-01 16:46:30', '2006-01-26 10:42:23', 0, 0, 'Actions', '', '', '', '', '<p>Durch die Actions dieses Moduls kann man auf den Inhalt der Texte zugreifen.<!--StartFragment --><!--StartFragment --></p> <p>Die Parameter der Actionaufrufe haben immer die selbe Bedeutung. Erklärung folgender Parameter:</p> <ul> <li><div><strong>''id_text''</strong> - Text ID auf die sich die Action bezieht</div></li><li><div><strong>''result''</strong> - Hier muss die Referenz eines vorher definierten leeren Arrays übergeben werden. <br />Ex.: &$this->tplVar </div></li><li><div><strong>''fields''</strong> - Array mit den Namen der Knoten Datenbank-Felder. Die möglichen Felder eines Textes sind: ''id_text'',''status'',''title'',''short_text'',''body'',''media_folder''</div></li><li><div><!--StartFragment --> <strong>''order''</strong> - Besteht das Resultat aus meheren Texten so wird in einem Array festgelegt wie die Reihenfolge des Resultates sortiert wird. Das erste Element im array definiert nach welchem Feld sortiert wird. Mögliche Felder sind <strong>''title'',''status''</strong>. Das zweite Element definiert die Reihenfolge. Mögliche Werte sind <strong>''asc'',''desc''</strong>. Also für aufsteigend und absteigend. Beispiel: array(''title'',''asc''). Wird order nicht angegeben so wird nach ''title'' ''asc'' das Resultat sortiert.<br /></div></li><li><div><strong>''status''</strong> - In diesem array wird definiert welchen Status der oder die Knoten haben müssen. Das erste Element im Array definiert die Beziehung. Eine von den folgenden sind möglich: ''<'',''>'',''='',''>='',''<=''. Das zweite element definiert den Statuswert. entweder 1 oder 2. Beispiel: array(''>'',1). Der oder die Knoten müssen einen Status grösser als 1 haben. <strong>1 bedeutet inaktiv. 2 bedeutet activ</strong>.<br /><br /></div></li> </ul> <p>Eine Actionklasse prüft immer die Parameter auf ihre Gültigkeit. Sollten Parameter nicht das richtige Format haben oder Parameter die fehlen, so erstellt die Klasse eine Exception. D.h. der Programablauf wird umgeleitet. Entweder wird eine Fehlermeldung angezeigt. Oder es nur ein Logeintrag geschrieben im Wurzelordner <strong>/logs. </strong>Es hängt davon ab wie die Konfigurationsvariablen für die Fehlerbehandlungengesetzt sind. </p> <h5>getText</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit dem Inhalt eines Textes</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''misc'', ''getText'',<br />                     array(''id_text'' => integer,<br />                           ''result''  => & array(),<br />                           ''fields''  => array() ));</pre> <h5>getTextes</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit dem Inhalt mehrer Texte</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''misc'', ''getTextes'',<br />                     array(''ids''    => array(id_x,id_y,id_z,..,..,..),<br />                           ''result'' => & array(),<br />                           ''order''  => array(),<br />                           ''fields'' => array() ));</pre> <h5>getAllThumbs</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Bilderdaten eines Textes</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''misc'', ''getAllThumbs'',<br />                         array(''id_text'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_text'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',''size'') ));<br /> </pre> <h5>getAllFiles</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Dateidaten eines Textes</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''misc'', ''getAllFiles'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_text'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'')));<br /> </pre> <h5>getPicture</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten eines Text-Bildes</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''misc'', ''getPicture'',<br />                         array(''id_pic'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_pic'',''id_text'',''title'',<br />                                                  ''description'',''file'',''height'',<br />                                                  ''width'',''rank'',''mime'',<br />                                                  ''size'',''media_folder'') ));<br /> </pre> <h5>getFile</h5> <p><strong>Beschreibung:</strong> Füllt ein Array mit den Daten einer Text-Datei</p> <p><strong>Aufrufsyntax:</strong></p> <pre>$this->model->action(''misc'', ''getFile'',<br />                         array(''id_file'' => integer,<br />                               ''result''  => & array,<br />                               ''fields''  => array(''id_file'',''id_text'',''title'',<br />                                                  ''description'',''file'',''rank'',<br />                                                  ''mime'',''size'',''media_folder'')));</pre><p> </p><h5>getKeywordIds</h5>            <p><strong>Beschreibung:</strong> Gibt alle SchlüsselwortID''s zurück die mit einem TextD verknüpft sind<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''misc'', ''getKeywordIds'',<br />                 array(''result''     => &$array(),<br />                       ''id_text''     => int,<br />                       ''key_status'' => array(''>|<|=|>=|<=|!='',<br />                                             1|2) // keyword status - optional<br />                       ) );<br /></pre><p> </p><h5>fromKeyword</h5>            <p><strong>Beschreibung:</strong> Gibt alle Texte, die mit den Schlüsselwort-Id''s die in einem Array definiert sind, zurück<br /><br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''misc'',''fromKeyword'',<br />                 array(''id_key_list'' => array( int, int,..,..,..),<br />                       ''result''      => & array,<br />                       ''status''      => array(''>|<|=|>=|<=|!='',1|2), // text status - optional<br />                       ''key_status''  => array(''>|<|=|>=|<=|!='',1|2), // keyword status - optional<br />                       ''exclude''     => array( integers ),           // exclude id_textes''s optional<br />                       ''order''   => array(''title'', ''asc|desc''), // optional<br />                       ''disable_sql_cache'' => TRUE,  // optional <br />                       ''fields   => array(''id_text'',''status'',<br />                                          ''title'',''description'',<br />                                          ''format'',''media_folder'') ));</pre>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (24, 43, 4, 1, 'en', '2005-09-02 13:34:00', '2005-09-02 13:34:15', '2006-01-26 10:57:56', 0, 0, 'Actions', '', '', '', '', '<p>This module provide a couple of action classes which you can use in public views. <br /> </p>       <h5>getArticle</h5>     <p><strong>Description:</strong> Fill an array with the content of an article<br /> </p>     <p><strong>Usage:</strong></p>     <pre>$this->model->action(''article'', ''getArticle'',<br />                     array(''id_article'' => integer,<br />                           ''result''     => & array(),<br />                           ''pubdate''    => array(''>|<|=|>=|<=|!='', <br />                                                 ''CURRENT_TIMESTAMP''),//optional<br />                           ''fields''     => array(''title'',''overtitle'',<br />                                                 ''subtitle'',''header'',<br />                                                 ''description'',''body'',<br />                                                 ''ps'',''pubdate'',<br />                                                 ''articledate'',''modifydate'',<br />                                                 ''logo'',''media_folder'') ));</pre>   <h5> </h5> <h5> </h5> <h5>getNodeArticles</h5>      <p><strong>Description:</strong> Fill an array with the content of articles that are linked to a navigation node. Default order is ''rank asc''<br /> </p>      <p><strong>Usage:</strong></p>      <pre>$this->model->action(''article'', ''getNodeArticles'',<br />                         array(''id_node'' => integer,<br />                               ''result''  => & array,<br />                               ''status''  => array(''>|<|=|>=|<=|!='', <br />                                                  0|1|2|3|4),// optional                           <br />                               ''pubdate'' => array(''>|<|=|>=|<=|!='', <br />                                                  ''CURRENT_TIMESTAMP''),//optional<br />                               ''order''   => array(''rank|title|<br />                                                  articledate|pubdate|modifydate<br />                                                  overtitle|subtitle'', <br />                                                  ''asc|desc''),  // optional<br />                               ''fields''  => array(''title'',''overtitle'',<br />                                                  ''subtitle'',''header'',<br />                                                  ''description'',''body'',<br />                                                  ''ps'',''pubdate'',<br />                                                  ''articledate'',''modifydate'',<br />                                                  ''logo'',''media_folder'') ));</pre> <h5> </h5> <h5>search</h5>               <p><strong>Description:</strong> Fill an array with the data of articles that match a search string<br /> </p>  <p><strong><strong>Usage:</strong></strong></p>  <strong>      </strong> <pre>$this->model->action(''article'', ''search'',<br />                         array(''result''  => & array,<br />                               ''search''  => string,   // search string<br />                               ''status''  => array(''>|<|=|>=|<=|!='', <br />                                                  0|1|2|3|4),// optional<br />                               ''nodeStatus'' => array(''>|<|=|>=|<=|!='',<br />                                                  0|1|2),// optional                           <br />                               ''pubdate'' => array(''>|<|=|>=|<=|!='', <br />                                                  ''CURRENT_TIMESTAMP''),//optional<br />                               ''order''   => array(''rank|title|<br />                                                  articledate|pubdate|modifydate<br />                                                  overtitle|subtitle'', <br />                                                  ''asc|desc''),  // optional<br />                               ''fields''  => array(''title'',''overtitle'',''id_node'',<br />                                                  ''subtitle'',''header'',<br />                                                  ''description'',''body'',<br />                                                  ''ps'',''pubdate'',<br />                                                  ''articledate'',''modifydate'',<br />                                                  ''logo'',''media_folder'') ));</pre> <h5>pager</h5>            <p><strong>Description:</strong> Build pagination links string<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre>$this->model->action(''article'',''pager'', <br />                      array(''result''     => & array,<br />                            ''id_node''    => integer,<br />                            ''search''     => string, // search string<br />                            ''status''     => array(''>|<|=|>=|<=|!='',<br />                                                  0|1|2|3|4 ),// optional<br />                            ''nodeStatus'' => array(''>|<|=|>=|<=|!='',<br />                                                  0|1|2),// optional                      <br />                            ''pubdate''     => array(''>|<|=|>=|<=|!='', <br />                                                   ''CURRENT_TIMESTAMP''),//optional<br />                            ''perPage''    => integer,<br />                            ''numPage''    => integer,<br />                            ''delta''      => integer,<br />                            ''url''        => string,<br />                            ''var_prefix'' => string,<br />                            ''css_class''  => string));  </pre> <h5>getStatus</h5>             <p><strong>Description:</strong> Return the article status and the article related node status <br />  $result[''nodeStatus'']<br />  $result[''articleStatus'']<br />      </p>                  <p><strong>Syntax:</strong></p>    <pre>$model->action(''article'',''getStatus'', <br />                array(''id_article'' => int,<br />                      ''result''     => & array));    </pre> <p> </p><h5>getKeywordIds</h5>            <p><strong>Description:</strong> Return all keywords that are related to a given article ID<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''article'', ''getKeywordIds'',<br />                 array(''result''     => &$array(),<br />                       ''id_article'' => int,<br />                       ''key_status'' => array(''>|<|=|>=|<=|!='',<br />                                             1|2) // keyword status - optional<br />                       ) );<br /></pre><p> </p><h5>fromKeyword</h5>            <p><strong>Description:</strong> Return all articles that are related to some given keyword ID''s defined in an array.<br />      </p>                 <p><strong>Syntax:</strong></p>   <pre> $model->action(''article'',''fromKeyword'',<br />                 array(''id_key_list'' => array( int, int,..,..,..),<br />                       ''result''      => & array,<br />                       ''status''      => array(''>|<|=|>=|<=|!='',<br />                                              1|2|3|4|5), // article status - optional<br />                       ''key_status''  => array(''>|<|=|>=|<=|!='',<br />                                              1|2), // keyword status - optional<br />                       ''node_status'' => array(''>|<|=|>=|<=|!='',<br />                                              1|2), // navigation node status -  optional<br />                       ''exclude''     => array( integers ), // exclude id_articles''s optional<br />                       ''order''   => array(''rank|title|<br />                                           articledate|pubdate|<br />                                           overtitle|subtitle'', ''asc|desc''), // optional<br />                       ''disable_sql_cache'' => TRUE,  // optional <br />                       ''fields   => array(''id_node'',''id_article'',''status'',''rank'',<br />                                          ''activedate'',''inactivedate'',''pubdate'',<br />                                          ''lang'',''title'',''overtitle'',<br />                                          ''subtitle'',''header'',''description'',<br />                                          ''body'',''ps'',''fulltextbody'',<br />                                          ''format'',''media_folder'') ));</pre>  <p> </p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (22, 6, 4, 1, 'en', '2005-09-01 16:47:00', '2005-09-01 16:47:29', '2006-01-24 22:29:38', 0, 0, 'Modul-Lizenz', '', '', '', '', '<p>Die mit Smart3 ausgelieferten <strong>Module</strong> stehen unter folgender Lizenz:</p>   <pre>		    GNU GENERAL PUBLIC LICENSE<br />		       Version 2, June 1991<br /><br /> Copyright (C) 1989, 1991 Free Software Foundation, Inc.<br />                       51 Franklin St, Fifth Floor, Boston, MA  02110-1301  USA<br /> Everyone is permitted to copy and distribute verbatim copies<br /> of this license document, but changing it is not allowed.<br /><br />			    Preamble<br /><br />  The licenses for most software are designed to take away your<br />freedom to share and change it.  By contrast, the GNU General Public<br />License is intended to guarantee your freedom to share and change free<br />software--to make sure the software is free for all its users.  This<br />General Public License applies to most of the Free Software<br />Foundation''s software and to any other program whose authors commit to<br />using it.  (Some other Free Software Foundation software is covered by<br />the GNU Library General Public License instead.)  You can apply it to<br />your programs, too.<br /><br />  When we speak of free software, we are referring to freedom, not<br />price.  Our General Public Licenses are designed to make sure that you<br />have the freedom to distribute copies of free software (and charge for<br />this service if you wish), that you receive source code or can get it<br />if you want it, that you can change the software or use pieces of it<br />in new free programs; and that you know you can do these things.<br /><br />  To protect your rights, we need to make restrictions that forbid<br />anyone to deny you these rights or to ask you to surrender the rights.<br />These restrictions translate to certain responsibilities for you if you<br />distribute copies of the software, or if you modify it.<br /><br />  For example, if you distribute copies of such a program, whether<br />gratis or for a fee, you must give the recipients all the rights that<br />you have.  You must make sure that they, too, receive or can get the<br />source code.  And you must show them these terms so they know their<br />rights.<br /><br />  We protect your rights with two steps: (1) copyright the software, and<br />(2) offer you this license which gives you legal permission to copy,<br />distribute and/or modify the software.<br /><br />  Also, for each author''s protection and ours, we want to make certain<br />that everyone understands that there is no warranty for this free<br />software.  If the software is modified by someone else and passed on, we<br />want its recipients to know that what they have is not the original, so<br />that any problems introduced by others will not reflect on the original<br />authors'' reputations.<br /><br />  Finally, any free program is threatened constantly by software<br />patents.  We wish to avoid the danger that redistributors of a free<br />program will individually obtain patent licenses, in effect making the<br />program proprietary.  To prevent this, we have made it clear that any<br />patent must be licensed for everyone''s free use or not licensed at all.<br /><br />  The precise terms and conditions for copying, distribution and<br />modification follow.<br />INSERT INTO `smart_article_article` VALUES (25, 20, 4, 8, 'en', '2005-09-03 11:01:00', '2005-09-03 11:01:47', '2006-02-15 21:31:36', 0, 0, 'Public Projects Views and Templates', '', '', '', '', '<p>You will find in the root folders <strong>/templates_smart</strong> and <strong>/views_smart</strong> all public views and templates. It''s an example of a project. You should insert the example content during the setup to check out how every thing works together. Based on this example you can adapt the views and templates to your own project conception. In this case you should store your project files in new folders; Ex.: <strong>/views_myproject</strong> and <strong>/templates_myproject</strong>, to prevent overwriting your modified files during the next framework update. <br />  </p>     <p>Usually every view class has a corresponding template file. The following views and templates are installed by default:</p> <ul> <li><strong>ViewArticle.php</strong> - <strong>tpl.article.php</strong> - This view fetch the demanded article data, assign template variables with this data and loads the corresponding template which outputs the article data.</li><li><strong>ViewError.php</strong> - <strong>tpl.error.php</strong> - Depending on the configuration settings this view output error messages.</li><li><strong>ViewHeader.php</strong> - <strong>tpl.header.php</strong> - This view build the output for every page header.<br /> </li><li><strong>ViewIndex.php</strong> - <strong>tpl.index.php</strong> - This view is responsible for the entry page.</li><li><strong>ViewLogout.php</strong> - Do some logout stuff. A logout link only appears in the left navigation bar if the webvisitor has previously login into the admin area. This view dont need any template.<br /> </li><li> <strong>ViewMainNavigation.php</strong> - <strong>tpl.mainNavigation.php</strong> - This view build the main navigation links that appears on the left side of every page</li><li><strong>ViewNode.php</strong> - <strong>tpl.node.php</strong> - This view fetch the demanded navigation node, articles of this node and links of this node.<br /> </li><li><strong>ViewPicture.php</strong> - <strong>tpl.picture.php</strong> - Show a picture in whole size.</li><li><strong>ViewSearch.php</strong> - <strong>tpl.search.php - </strong>Show the articles that match a search string<br />   </li><li><strong>ViewSimpleNode.php</strong> - <strong>tpl.simpleNode.php</strong> - Show a simple navigation node that has no subnodes or articles, links.....<br /> </li><li><strong>ViewSitemap.php</strong> - <strong>tpl.sitemap.php</strong> - Show the whole navigation node tree with articles.</li> </ul>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (27, 2, 4, 5, 'en', '2005-10-16 21:48:00', '2005-10-16 21:48:59', '2006-03-05 19:20:57', 0, 0, 'Der Controller', '', '', '', '', '<p>Der Controller ist notwendig damit Alles seinen richtigen Weg geht. Er nimmt als erstes eine Anfrage an das System entgegen und f&uuml;hrt je nach seiner Konzeption u.a. folgende Operationen aus: </p>   <ul> <li>Sendet eine broadcast init Action an alle Module <br />   </li><li>Erstellt einige Basisinstanzen. U.a die des Models.<br />   </li><li>Bestimmt u.a. den Workflow in den Viewklassen. Also welche Methoden wann ausgef&uuml;hrt werden.</li><li>Sorgt bei Bedarf daf&uuml;r dass eine Template gerendert wird<br />   </li> </ul>   <p>In Smart3 k&ouml;nnen unterschiedliche Controllertypen erstellt werden die zum l&ouml;sen ganz spezifischer Aufgaben geeignet sind. Zur Zeit gib es 5 verschiedene Controllertypen.</p>   <ol> <li>Public Web Controller - <strong>index.php</strong> - Dieser nimmt die &ouml;ffentliche Anfrage an ein Webseite entgegen.</li><li>Admin Web Controller - <strong>admin.php</strong> - Dieser nimmt die Anfragen entgegen die an das Administrationsinterface gerichtet sind</li><li>CLI Controller - <strong>cli.php</strong> - Durch diesen nimmt das System eine Anfrage z.B. von einer Console aus entgegen.</li><li>XML_RPC Controller - <strong>rpcserver.php</strong> - Dieses eine XML RPC Server.</li><li>Der Ajax server Controlle. siehe <a href="index.php?id_article=50">Smart3 und Ajax</a> <br /> </li> </ol>    <h5>Der Cli Controller </h5>   <p>Durch den cli Controller kann das Framework nicht nur in einer Webumgebung eingesetzt werden sondern auch f&uuml;r komplexe Shell Aufgaben wie das etwa mit der unix bash m&ouml;glich ist. &Uuml;ber cli.php wird eine View angesprochen in der man alle Actions benutzen kann. Es wird allerdings keine Template gerendert.</p> <p>Das Beispiel cli View in /views_cli/ViewCliTest.php wird unter *nix folgendermassen aufgerufen: </p>   <pre>/usr/bin/php ./cli.php -v cliTest [ -f id_node title ] [ -idn 0 ]<br />oder<br />/usr/bin/php ./cli.php --view cliTest [ --fields id_node title ] [ --id_node 0 ]  </pre>   <p>In der Viewklasse funktioniert fast alles gleich wie das bei den 2 anderen Controllern der Fall ist. Mit Ausnahme dass:</p>   <ul> <li>Caching ausgeschaltet ist <br />   </li><li>Keine Template gerender werden kann</li><li>Keine Append Filter Methode zur Verf&uuml;gung steht</li> </ul> <p>Als Komandoargumentparse kommt <a href="http://pear.php.net/package/Console_Getargs" target="_blank">Console_Getargs</a>  zum Einsatz.</p> <h5>Der XML_RPC Controller</h5>   <p>Die Datei <strong>rpcserver.php</strong> im Wurzelverzeichniss ist ein Server &uuml;ber den Anfragen entgegengenommen werden. RPC ist eine standadisierte Schnittstelle durch die Funktions- oder Methodenaufrufe &uuml;ber ein Netzwerk get&auml;tigt werden k&ouml;nnen. Weitere Informationen zu <a href="http://www.xmlrpc.com" target="_blank">RPC</a>. </p> <p>  Smart3 setzt auf die PEAR <a href="http://pear.php.net/package/XML_RPC/" target="_blank">Xml_RPC</a>  Library<span class="surname">. Die entsprechenden Views liegen im <strong>/views_rpcserver</strong> Ordner. Zur Zeit gibt es nur eine View als Beispiel. Durch diese kann man eine bestimmte Anzahl von Artikeln aufrufen, geordnet nach dem Publizierungs- oder Modifizierungsdatum. Den Server kann man testen durch den RPC-Klienten in <a href="http://www.smart3.org/tests/XmlRpcClient.php" target="_blank">/tests/XmlRpcClient.php</a>. Durch diesen werden die 8 letzten publizierten Artikel von der Smart3 Webseite abgerufen.</span></p> <h6>Notiz: </h6>   <p>Die beiden Controllertypen Cli und XML RPC haben vorl&auml;ufig noch einen experimentellen Charakter. Aber es wird hier demonstriert was Alles mit Smart3 m&ouml;glich ist.<br /> </p> <p>&nbsp;</p> <p>&nbsp;</p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (29, 45, 4, 0, 'en', '2006-01-24 12:20:00', '2006-01-24 12:20:03', '2006-03-05 18:50:52', 0, 0, 'Change Log', '', '', '', '', '<pre>0.2.6b<br />------<br />- update to TinyMce 2.0.4<br />- added bolg features in article module.<br />  Web users can add comments to articles.&nbsp;</pre><pre>0.2.5b<br />------<br />- update to TinyMce 2.0.3<br />- added GD check in setup module<br />- added TinyMce cache<br />- added rss builder action<br />- added ajax server and usage examples (experimental)<br />- added Each article can be assigned with an individual view <br /></pre><pre>0.2.4b<br />------<br />- upgrade to Tinymce 2.0.1<br />- auto public view detection related to articles<br /><br />0.2.3b<br />--------<br />- fixed self reference of nodes and keywords<br />- fixed expired session time settings<br />- fixed small file upload bug<br />- added session expire time in options </pre><pre>0.2.2a<br />------<br />- added keyword module<br />- added keyword features in modules: misc,article,navigation and link<br />- added separated public view and template to show extern links</pre><pre>0.2.1a<br />------<br />- in modules &quot;navigation&quot; and &quot;article&quot; you can now set content to protect,<br />  means only registered user have access to.<br />- added <strong>C L I</strong> (command line interface) controller to build shell applications: <br />  You can find in the root folder the<br />  cli.php controller and the related views in /views_cli as an example<br />  of how to use it.<br />  Example: /bin/php cli.php --view cliTest<br />  As with other controllers, you can use all actions from within cli views<br />  and thus execute complex shell operations.<br />- added rpcserver.php (XML_RPC Server) controller and related views in /views_rpcserver.<br />- added /tests/XmlRpcClient.php.<br /><br /> 0.2.0.9a<br /> --------<br /> - fixed some nasty warnings under php version 5.0.5<br /> - fixed sql language encoding bug<br /> - added article select with pubdate option<br /> - added ActionNavigationGetNodeStatus.php action class<br /> - modified public error template<br /><br /> 0.2.0.8a<br /> --------<br /> - fixed small error reporting bug in error handler<br /> - added Smarty template engine support. Now you have the<br />   choice between php and Smarty as template engine.<br /><br /> 0.2.0.7a<br /> --------<br /> - fixed small setup bug<br /> - minor fixes<br /><br /> 0.2.0.6a<br /> --------<br /> - fixed bug in misc module (only on *nix)<br /> - added javacript compression for tinymce editor<br /> - fixed some links in the doc<br /> - fixed small error log bug<br /><br /> 0.2.0.5a<br /> --------<br /> - fixed image bug in article,navigation and misc module<br /> - added admin view folder select in configuration array.<br />   It&#39;s now possible to develop more than one admin view<br />   and its templates. Means switching between multiple<br />   admin layouts.<br /> - added support of the mysql client extension.<br />   It supports now mysql and mysqli.<br /> <br /> 0.2.0.4a<br /> --------<br /> - enhanced the article module<br /> - added pager classes<br /> - minor changes<br /> - fixed a few bugs<br /><br /> 0.2.0.3a<br /> --------<br /> - added article module<br /> - added article search public view.<br /> - fixed a few bugs <br /><br /> 0.2.0.2a<br /> --------<br /> - added simple link module<br /> - added sending system messages by email. see: my_config_dist.php.<br /> - added disallowed upload files in options<br /> - fixed modules data dir handling<br /><br /> 0.2.0.1a<br /> --------<br /> - it is possible to insert sample content on the setup page.<br /><br /> 0.2a<br /> ----<br /> - Initial release</pre>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (30, 44, 4, 0, 'en', '2006-01-24 13:39:00', '2006-01-24 13:39:07', '2006-02-11 14:28:05', 0, 0, 'Features', '', '', '', '', '<ul><li>Very simple implementation of the ModelViewController pattern</li><li>Each view can have its own authentication mechanism, prepended filters, appened filters and template render engine type.</li><li>Loose coupling between the view and the model (module actions)<br />   </li><li>View caching</li><li>Different controller types support (Web, Cli, RpcServer, AjaxServer)<br /></li><li>Fast</li><li>Moderated memory  consumtion<br /> </li><li>Module support</li><li>Plug&amp;Play of entier modules and its action classes</li><li>Rapide application development</li></ul>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (31, 35, 4, 0, 'en', '2006-01-24 14:05:00', '2006-01-24 14:05:32', '2006-02-19 15:54:35', 0, 0, 'Support', '', '', '', '', '<p class="smart">For questions, suggestions and critics you should use the Project forum (english, german or french):<br /> <a href="http://forum.smart3.org" target="_blank" title="Smart3 Forum">http://forum.smart3.org</a></p>   <p class="smart">If you need professional support, mailto:</p>   <p class="smart">Project leader: Armand Turpel<br /> <a href="mailto:framework@smart3.org?subject=SMART3" title="Email of the project leader">framework@smart3.org</a></p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (32, 18, 4, 0, 'en', '2006-01-24 14:19:00', '2006-01-24 14:19:17', '2006-02-19 15:55:17', 0, 0, 'Download', '', '', '', '', '<p>You can download the current version at Berlios, where you also can find the Smart3 Subversion repository, or at  Sourceforge.</p><p><a href="http://developer.berlios.de/projects/smart/" target="_blank" title="Donwload Smart3">Download Smart3 from Berlios</a></p><p><a href="http://sourceforge.net/projects/smart3" target="_blank">Download Smart3 from Sourceforge</a>  </p><p>The core Framework requires php5. The delivered modules require MySql 4.1</p><p>The size of the whole package is about 7mb. But the most space (3mb) is used by TinyMCE and some Pear packages. The core framework without the modules is about 50kb.<br /> </p><p>Smart3 is developed under the Subversion version control. You can browse the repository <a href="http://svn.berlios.de/viewcvs/smart/smartframe/trunk/" target="_blank" title="Smart3 Subversion repository">here</a>.</p><p>For reporting bugs, suggestions and questions please use the Smart3 <a href="http://forum.smart3.org" target="_blank" title="Smart3 Forum">forum</a>. </p>', '', 2, '', '');
INSERT INTO `smart_article_article` VALUES (33, 15, 4, 0, 'en', '2006-01-24 14:35:00', '2006-01-24 14:35:01', '2006-01-24 14:35:23', 0, 0, 'License', '', '', '', '', '<div class="text"><p class="smart">The Smart3 Framework without modules is released under the following License: </p><p class="smart">GNU LESSER GENERAL PUBLIC LICENSE<br /> Version 2.1, February 1999</p>  <p class="smart">Copyright (C) 1991, 1999 Free Software Foundation, Inc.<br /> 59 Temple Place, Suite 330, Boston, MA  02111-1307  USA<br /> Everyone is permitted to copy and distribute verbatim copies<br /> of this license document, but changing it is not allowed.</p>  <p class="smart">[This is the first released version of the Lesser GPL.  It also counts<br /> as the successor of the GNU Library Public License, version 2, hence<br /> the version number 2.1.]</p>  <p class="smart">Preamble</p>  <p class="smart">The licenses for most software are designed to take away your<br /> freedom to share and change it.  By contrast, the GNU General Public<br /> Licenses are intended to guarantee your freedom to share and change<br /> free software--to make sure the software is free for all its users.</p>  <p class="smart">This license, the Lesser General Public License, applies to some<br /> specially designated software packages--typically libraries--of the<br /> Free Software Foundation and other authors who decide to use it.  You<br /> can use it too, but we suggest you first think carefully about whether<br /> this license or the ordinary General Public License is the better<br /> strategy to use in any particular case, based on the explanations below.</p>  <p class="smart">When we speak of free software, we are referring to freedom of use,<br /> not price.  Our General Public Licenses are designed to make sure that<br /> you have the freedom to distribute copies of free software (and charge<br /> for this service if you wish); that you receive source code or can get<br /> it if you want it; that you can 