INSERT INTO `smart_navigation_node` VALUES (52, 37, 37, 5, 2, 1, '2006-02-07 17:25:02', 'en', 'My Digg Links', '', '<p><a target="_blank" href="http://www.digg.com">Digg</a>  is a technology news website that combines social bookmarking, blogging, RSS, and non-hierarchical editorial control. With digg, users submit stories for review, but rather than allow an editor to decide which stories go on the homepage, the users do.</p><p><a target="_blank" href="http://www.digg.com"><br /> </a> </p>  <script type="text/javascript" language="JavaScript" src="http://digg.com/jsdig/verbose/20/atur" />', 0, '', '');
